import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-2339 Duties of the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2339.md","filePath":"vacode/2.2-2339.md"}'),i={name:"vacode/2.2-2339.md"},n=o('<h1 id="§-2-2-2339-duties-of-the-authority" tabindex="-1">§ 2.2-2339 Duties of the Authority. <a class="header-anchor" href="#§-2-2-2339-duties-of-the-authority" aria-label="Permalink to &quot;§ 2.2-2339 Duties of the Authority.&quot;">​</a></h1><p>The Authority shall have the power and duty:</p><ol><li><p>To do all things necessary and proper to further an appreciation of the contributions of the first permanent English-speaking settlers as well as the Virginia Indians to the building of our Commonwealth and nation, to commemorate the establishment of the first coastal fortification in the English-speaking New World, to commemorate the lives of prominent Virginians who were connected to the largest moated fortification in the United States, to commemorate the important role of African Americans in the history of the site, including the &quot;Contraband&quot; slave decision in 1861 that earned Fort Monroe the designation as &quot;Freedom&#39;s Fortress,&quot; to commemorate Old Point Comfort&#39;s role in establishing international trade and British maritime law in Virginia, and to commemorate almost 250 years of continuous service as a coastal defense fortification of the United States of America;</p></li><li><p>To hire and develop a professional staff including an executive director and such other staff as is necessary to discharge the responsibilities of the Authority;</p></li><li><p>To establish personnel policies and benefits for staff;</p></li><li><p>To oversee the preservation, conservation, protection, and maintenance of the Commonwealth&#39;s natural resources and real property interests at Fort Monroe and the renewal of Fort Monroe as a vibrant and thriving community;</p></li><li><p>To adopt an annual budget, which shall be submitted to the Chairmen of the Senate Committee on Finance and Appropriations and the House Committee on Appropriations and the Department of Planning and Budget by July 1 of each year;</p></li><li><p>To provide for additional, more complete, or more timely services than are generally available in the City of Hampton as a whole; and</p></li><li><p>To serve as the Commonwealth&#39;s management agent for all the land in the Area of Operation and for the implementation of actions and fulfillment of federal and state obligations for public and private land under the Fort Monroe Master Plan, Programmatic Agreement, Design Standards, Reuse Plan, State Memorandum of Understanding, and any other agreements regarding Fort Monroe to which the Commonwealth is a party, ensuring adherence to the findings, declarations, and policies set forth in this article, unless the Commonwealth and the Authority specifically agree in writing to the contrary.</p></li></ol><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0436" target="_blank" rel="noreferrer">436</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0482" target="_blank" rel="noreferrer">482</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0676" target="_blank" rel="noreferrer">676</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0681" target="_blank" rel="noreferrer">681</a>.</p>',4),r=[n];function s(l,h,d,c,f,p){return t(),a("div",null,r)}const u=e(i,[["render",s]]);export{g as __pageData,u as default};
