import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2368 Duties of the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2368.md","filePath":"vacode/2.2-2368.md"}'),r={name:"vacode/2.2-2368.md"},o=a('<h1 id="§-2-2-2368-duties-of-the-authority" tabindex="-1">§ 2.2-2368 Duties of the Authority. <a class="header-anchor" href="#§-2-2-2368-duties-of-the-authority" aria-label="Permalink to &quot;§ 2.2-2368 Duties of the Authority.&quot;">​</a></h1><p>The Authority shall:</p><ol><li><p>Establish specific criteria and procedures for awards from the Fund;</p></li><li><p>Establish requirements for the submission of funding requests;</p></li><li><p>Evaluate funding requests in accordance with the criteria established by the Authority and the provisions of this article;</p></li><li><p>Make awards from the Fund in a manner that distributes funds equitably among all community services board regions of the Commonwealth, including the establishment of mandatory minimum percentages of funds to be awarded from the Commonwealth to each participating locality;</p></li><li><p>Evaluate the implementation and results of all efforts receiving support from the Authority; and</p></li><li><p>Administer the Fund in accordance with the provisions of this article.</p></li></ol><p>2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0306" target="_blank" rel="noreferrer">306</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0307" target="_blank" rel="noreferrer">307</a>.</p>',4),s=[o];function n(l,h,c,d,u,p){return t(),i("div",null,s)}const _=e(r,[["render",n]]);export{m as __pageData,_ as default};
