import{_ as e,o as r,c as n,O as a}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 2.2-2419 Issuance of refunding bonds by the Treasury Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2419.md","filePath":"vacode/2.2-2419.md"}'),o={name:"vacode/2.2-2419.md"},t=a('<h1 id="§-2-2-2419-issuance-of-refunding-bonds-by-the-treasury-board" tabindex="-1">§ 2.2-2419 Issuance of refunding bonds by the Treasury Board. <a class="header-anchor" href="#§-2-2-2419-issuance-of-refunding-bonds-by-the-treasury-board" aria-label="Permalink to &quot;§ 2.2-2419 Issuance of refunding bonds by the Treasury Board.&quot;">​</a></h1><p>The Board may, with the consent of the Governor, sell and issue refunding bonds of the Commonwealth to refund any or all of the Commonwealth&#39;s bonds or other debt. The aggregate principal amount of such refunding bonds shall not exceed the amount required to redeem or otherwise provide for the payment of the unpaid principal of and interest on and any redemption premium payable on the bonds to be refunded to their date of redemption or payment, plus all expenses incurred in such refunding transaction.</p><p>1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0636" target="_blank" rel="noreferrer">636</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0656" target="_blank" rel="noreferrer">656</a>, § 2.1-179.4; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',3),s=[t];function i(d,c,l,f,h,u){return r(),n("div",null,s)}const b=e(o,[["render",i]]);export{_ as __pageData,b as default};
