import{_ as e,o as a,c as r,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 2.2-2448 Virginia-Asian Advisory Board established; purpose.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2448.md","filePath":"vacode/2.2-2448.md"}'),t={name:"vacode/2.2-2448.md"},o=i('<h1 id="§-2-2-2448-virginia-asian-advisory-board-established-purpose" tabindex="-1">§ 2.2-2448 Virginia-Asian Advisory Board established; purpose. <a class="header-anchor" href="#§-2-2-2448-virginia-asian-advisory-board-established-purpose" aria-label="Permalink to &quot;§ 2.2-2448 Virginia-Asian Advisory Board established; purpose.&quot;">​</a></h1><p>The Virginia-Asian Advisory Board (the Board) is hereby established as an advisory board within the meaning of § <a href="http://law.lis.virginia.gov/vacode/2.2-2100/" target="_blank" rel="noreferrer">2.2-2100</a> in the executive branch of state government. The purpose of the Board shall be to advise the Governor on ways to improve economic and cultural links between the Commonwealth and Asian nations, with a focus on the areas of commerce and trade, art and education, and general government, and on issues affecting the Asian-American communities in the Commonwealth.</p><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0566" target="_blank" rel="noreferrer">566</a>, § 9-400; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0971" target="_blank" rel="noreferrer">971</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1005" target="_blank" rel="noreferrer">1005</a>.</p>',3),n=[o];function s(d,l,c,h,p,_){return a(),r("div",null,n)}const f=e(t,[["render",s]]);export{v as __pageData,f as default};
