import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-2450 Powers and duties of the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2450.md","filePath":"vacode/2.2-2450.md"}'),r={name:"vacode/2.2-2450.md"},i=o('<h1 id="§-2-2-2450-powers-and-duties-of-the-board" tabindex="-1">§ 2.2-2450 Powers and duties of the Board. <a class="header-anchor" href="#§-2-2-2450-powers-and-duties-of-the-board" aria-label="Permalink to &quot;§ 2.2-2450 Powers and duties of the Board.&quot;">​</a></h1><p>The Board shall:</p><ol><li><p>Undertake studies and gather information and data in order to accomplish its purposes as set forth in § <a href="http://law.lis.virginia.gov/vacode/2.2-2448/" target="_blank" rel="noreferrer">2.2-2448</a>, and to formulate and present its recommendations to the Governor.</p></li><li><p>Apply for, accept, and expend gifts, grants, or donations from public, quasi-public or private sources, including any matching funds as may be designated in the Appropriation Act, to enable it to better carry out its purposes.</p></li><li><p>Report annually its findings and recommendations to the Governor. The Board may make interim reports to the Governor as it deems advisable.</p></li><li><p>Account annually on its fiscal activities, including any matching funds received or expended by the Board.</p></li></ol><p>2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0566" target="_blank" rel="noreferrer">566</a>, § 9-402.</p>',4),n=[i];function s(d,l,c,p,h,_){return a(),t("div",null,n)}const m=e(r,[["render",s]]);export{u as __pageData,m as default};
