import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 2.2-2453 Compensation; expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2453.md","filePath":"vacode/2.2-2453.md"}'),n={name:"vacode/2.2-2453.md"},i=t('<h1 id="§-2-2-2453-compensation-expenses" tabindex="-1">§ 2.2-2453 Compensation; expenses. <a class="header-anchor" href="#§-2-2-2453-compensation-expenses" aria-label="Permalink to &quot;§ 2.2-2453 Compensation; expenses.&quot;">​</a></h1><p>Legislative members of the Board shall receive such compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/30-19.12/" target="_blank" rel="noreferrer">30-19.12</a>, and nonlegislative citizen members shall receive such compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> for the performance of their duties. All members shall be reimbursed for all reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>. Funding for the costs of compensation and expenses of the members shall be provided by the Department of Veterans Services.</p><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP1000" target="_blank" rel="noreferrer">1000</a>.</p>',3),o=[i];function s(l,c,p,d,h,f){return a(),r("div",null,o)}const g=e(n,[["render",s]]);export{v as __pageData,g as default};
