import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2454 Powers and duties of Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2454.md","filePath":"vacode/2.2-2454.md"}'),a={name:"vacode/2.2-2454.md"},o=r('<h1 id="§-2-2-2454-powers-and-duties-of-board" tabindex="-1">§ 2.2-2454 Powers and duties of Board. <a class="header-anchor" href="#§-2-2-2454-powers-and-duties-of-board" aria-label="Permalink to &quot;§ 2.2-2454 Powers and duties of Board.&quot;">​</a></h1><p>The Board shall have the power and duty to:</p><ol><li><p>Advise and make recommendations to the Commissioner of Veterans Services upon such matters as may arise in the performance of his duties;</p></li><li><p>Investigate issues related to the provision of care and services to veterans, upon request of the Commissioner of Veterans Services or the Governor;</p></li><li><p>Study all matters affecting the welfare of Virginia citizens who are veterans or dependents or survivors of such veterans, and make recommendations to the Commissioner of the Department of Veterans Services;</p></li><li><p>Develop recommendations for policies and procedures related to the efficient and effective delivery of the services provided by the Department of Veterans Services;</p></li><li><p>Establish policies related to the coordinated delivery of veterans services, in consultation with those agencies, entities, and organizations, including counties, cities, towns or other political subdivisions of the Commonwealth capable of providing such services;</p></li><li><p>Monitor the administration of all laws concerning veterans and their dependents;</p></li><li><p>Review and advise the Commissioner of the Department of Veterans Services on the Department&#39;s strategic plan;</p></li><li><p>Based on rigorous cost-benefit-value analysis, provide recommendations to the Department of Veterans Services regarding future projects and the acquisition of facilities that may benefit the State&#39;s veterans, including but not limited to veterans cemeteries and veterans care centers; and</p></li><li><p>Provide recommendations to the Department of Veterans Services and the Veterans Services Foundation created in § <a href="http://law.lis.virginia.gov/vacode/2.2-2715/" target="_blank" rel="noreferrer">2.2-2715</a> regarding gifts, grants, and other resources from public and private entities and organizations to support veterans services.</p></li></ol><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>.</p>',4),n=[o];function s(d,c,l,p,f,h){return t(),i("div",null,n)}const u=e(a,[["render",s]]);export{m as __pageData,u as default};
