import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-2481 Powers and duties of the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2481.md","filePath":"vacode/2.2-2481.md"}'),i={name:"vacode/2.2-2481.md"},r=o('<h1 id="§-2-2-2481-powers-and-duties-of-the-board" tabindex="-1">§ 2.2-2481 Powers and duties of the Board. <a class="header-anchor" href="#§-2-2-2481-powers-and-duties-of-the-board" aria-label="Permalink to &quot;§ 2.2-2481 Powers and duties of the Board.&quot;">​</a></h1><p>The Board shall have the power and duty to:</p><ol><li><p>Advise the Governor, the Secretaries of Health and Human Resources, Education, and Natural and Historic Resources, the Assistant to the Governor for Commonwealth Preparedness, the State Board of Social Services, and other appropriate officials on national and community service programs in Virginia in order to (i) fulfill the responsibilities and duties prescribed by the federal Corporation for National and Community Service and (ii) develop, implement, and evaluate the Virginia State Service Plan, which outlines strategies for supporting and expanding national and community service throughout the Commonwealth.</p></li><li><p>Promote the use of AmeriCorps programs to meet Virginia&#39;s most pressing human, educational, environmental, and public safety needs.</p></li><li><p>Collaborate with the Department of Social Services and other public and private entities to recognize and call attention to the significant community service contributions of Virginia citizens and organizations.</p></li><li><p>Assist the Department of Social Services to promote the involvement of faith-based organizations in community and national service efforts.</p></li><li><p>Submit an annual report to the Governor and the General Assembly for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports. The chairman of the Board shall submit to the Governor and the General Assembly an annual executive summary of the interim activity and work of the Board no later than the first day of each regular session of the General Assembly. The executive summary shall be submitted for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports and shall be posted on the General Assembly&#39;s website.</p></li></ol><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0026" target="_blank" rel="noreferrer">26</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0452" target="_blank" rel="noreferrer">452</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0401" target="_blank" rel="noreferrer">401</a>.</p>',4),n=[r];function s(l,d,c,h,p,m){return t(),a("div",null,n)}const v=e(i,[["render",s]]);export{u as __pageData,v as default};
