import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-2499.7 Powers and duties of the Board.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2499.7.md","filePath":"vacode/2.2-2499.7.md"}'),i={name:"vacode/2.2-2499.7.md"},r=o('<h1 id="§-2-2-2499-7-powers-and-duties-of-the-board" tabindex="-1">§ 2.2-2499.7 Powers and duties of the Board. <a class="header-anchor" href="#§-2-2-2499-7-powers-and-duties-of-the-board" aria-label="Permalink to &quot;§ 2.2-2499.7 Powers and duties of the Board.&quot;">​</a></h1><p>The Cannabis Equity Reinvestment Board shall have the following powers and duties:</p><ol><li><p>Support persons, families, and communities historically and disproportionately targeted and affected by drug enforcement;</p></li><li><p>Develop and implement scholarship programs and educational and vocational resources for historically marginalized persons, including persons in foster care, who have been adversely impacted by substance use individually, in their families, or in their communities.</p></li><li><p>Develop and implement a program to award grants to support workforce development programs, mentoring programs, job training and placement services, apprenticeships, and reentry services that serve persons and communities historically and disproportionately targeted by drug enforcement.</p></li><li><p>Administer the Cannabis Equity Reinvestment Fund established pursuant to § <a href="/vacode/2.2-2499.8/">2.2-2499.8</a>.</p></li><li><p>Collaborate with the Board of Directors of the Virginia Cannabis Control Authority and the Office of Diversity, Equity, and Inclusion as necessary to implement programs and provide recommendations in line with the purpose of this article.</p></li><li><p>Submit an annual report to the Governor and the General Assembly for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports. The chairman shall submit to the Governor and the General Assembly an annual executive summary of the interim activity and work of the Council no later than the first day of each regular session of the General Assembly. The executive summary shall be submitted as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports and shall be posted on the General Assembly&#39;s website.</p></li><li><p>Perform such other activities and functions as the Governor and General Assembly may direct.</p></li></ol><p>2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0550" target="_blank" rel="noreferrer">550</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>.</p>',4),n=[r];function s(l,d,p,c,h,m){return t(),a("div",null,n)}const v=e(i,[["render",s]]);export{f as __pageData,v as default};
