import{_ as e,o as a,c as s,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 2.2-2540 (Expires July 1, 2026) Compensation; expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2540.md","filePath":"vacode/2.2-2540.md"}'),t={name:"vacode/2.2-2540.md"},n=o('<h1 id="§-2-2-2540-expires-july-1-2026-compensation-expenses" tabindex="-1">§ 2.2-2540 (Expires July 1, 2026) Compensation; expenses. <a class="header-anchor" href="#§-2-2-2540-expires-july-1-2026-compensation-expenses" aria-label="Permalink to &quot;§ 2.2-2540 (Expires July 1, 2026) Compensation; expenses.&quot;">​</a></h1><p>Legislative members of the Commission shall receive such compensation as provided in § <a href="/vacode/30-19.12/">30-19.12</a>. Nonlegislative citizen members shall not receive compensation. All members shall be reimbursed for reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="/vacode/2.2-2813/">2.2-2813</a> and <a href="/vacode/2.2-2825/">2.2-2825</a>. Compensation to members of the General Assembly for attendance at official meetings of the Commission shall be paid by the offices of the Clerk of the House of Delegates or Clerk of the Senate, as applicable. Reimbursement for the reasonable and necessary expenses of nonlegislative citizen members of the Commission shall be paid by the Halifax County Industrial Development Authority.</p><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0705" target="_blank" rel="noreferrer">705</a>.</p>',3),i=[n];function r(l,c,p,m,f,h){return a(),s("div",null,i)}const u=e(t,[["render",r]]);export{_ as __pageData,u as default};
