import{_ as e,o as t,c as a,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-2541 (Expires July 1, 2026) Powers and duties of the Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2541.md","filePath":"vacode/2.2-2541.md"}'),i={name:"vacode/2.2-2541.md"},s=o('<h1 id="§-2-2-2541-expires-july-1-2026-powers-and-duties-of-the-commission" tabindex="-1">§ 2.2-2541 (Expires July 1, 2026) Powers and duties of the Commission. <a class="header-anchor" href="#§-2-2-2541-expires-july-1-2026-powers-and-duties-of-the-commission" aria-label="Permalink to &quot;§ 2.2-2541 (Expires July 1, 2026) Powers and duties of the Commission.&quot;">​</a></h1><p>The Commission shall have the power and duty to:</p><ol><li><p>Establish a public-private partnership to create the Henrietta Lacks Life Sciences Center as a cancer research and treatment center located in Halifax County and designed to (i) transform and accelerate cancer research and treatment through the use of biodata tools, (ii) provide tailored cancer treatment medicine to an underserved portion of rural Southside Virginia, and (iii) incubate new biotech businesses across the Southside Virginia region; and</p></li><li><p>Submit to the Governor and the General Assembly an annual report for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports. The chairman of the Commission shall submit to the Governor and the General Assembly an annual executive summary of the interim activity and work of the Commission no later than the first day of each regular session of the General Assembly. The executive summary shall be submitted for publication as a report document as provided in the procedures of the Division of Legislative Automated Systems for the processing of legislative documents and reports and shall be posted on the General Assembly&#39;s website.</p></li></ol><p>2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0705" target="_blank" rel="noreferrer">705</a>.</p>',4),r=[s];function n(l,d,c,h,m,p){return t(),a("div",null,r)}const _=e(i,[["render",n]]);export{f as __pageData,_ as default};
