import{_ as e,o as i,c as o,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-2548 (Expires July 1, 2027) Powers and duties of the Commission.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2548.md","filePath":"vacode/2.2-2548.md"}'),a={name:"vacode/2.2-2548.md"},n=t('<h1 id="§-2-2-2548-expires-july-1-2027-powers-and-duties-of-the-commission" tabindex="-1">§ 2.2-2548 (Expires July 1, 2027) Powers and duties of the Commission. <a class="header-anchor" href="#§-2-2-2548-expires-july-1-2027-powers-and-duties-of-the-commission" aria-label="Permalink to &quot;§ 2.2-2548 (Expires July 1, 2027) Powers and duties of the Commission.&quot;">​</a></h1><p>The Commission shall have the power and duty to:</p><ol><li><p>Formulate and implement a program for the inclusive observance of the 250th anniversary of the independence of the United States and the Revolutionary War in Virginia, including (i) civic, cultural, and historical education and scholarship concerning the ideals of the American Revolution and their contemporary relevance; (ii) visitation of museums and historic sites, including battlefields; (iii) creation and publication of historical documents and studies; (iv) cooperation with agencies responsible for the preservation or restoration of historic sites, buildings, art, and artifacts; (v) establishment of exhibitions and interpretive and wayfinding signage; (vi) arrangement of appropriate public ceremonies; (vii) a comprehensive marketing and tourism campaign encompassing calendar year 2025 through calendar year 2026; and (viii) the general dissemination of public information regarding Virginia&#39;s involvement in the American Revolution and its legacy today;</p></li><li><p>Submit to the Governor and the General Assembly an annual executive summary of the interim activity and work of the Commission no later than the first day of each regular session of the General Assembly;</p></li><li><p>Solicit, accept, use, and dispose of funds appropriated by the General Assembly and any gifts, grants, donations, bequests, or other funds received by the Commission for the purpose of aiding or facilitating its work;</p></li><li><p>Appoint and establish an advisory council, to be led by the Commission member representing Gunston Hall, composed of nonlegislative citizen members at large who have a knowledge of relevant history or expertise in areas useful to the work of the Commission, including a representative of the Sons of the Revolution in the Commonwealth of Virginia, a representative of the Virginia Daughters of the American Revolution, and a representative of the National Washington-Rochambeau Revolutionary Route Association. The advisory council shall make recommendations and provide comment as requested by the Commission. The Commission may from time to time appoint, add, or remove members of the advisory council. Members of the advisory council shall serve without compensation or reimbursement;</p></li><li><p>Appoint and establish an executive committee composed of members of the Commission, to include the Commission&#39;s chair and vice chair and one representative designated by each of the following: the Jamestown-Yorktown Foundation, the Virginia Museum of History &amp; Culture, and Gunston Hall; and</p></li><li><p>Perform such other duties, functions, and activities as may be necessary to facilitate and implement the objectives of the Commission.</p></li></ol><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0914" target="_blank" rel="noreferrer">914</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0915" target="_blank" rel="noreferrer">915</a>.</p>',4),s=[n];function r(l,m,c,d,h,p){return i(),o("div",null,s)}const v=e(a,[["render",r]]);export{f as __pageData,v as default};
