import{_ as e,o,c as i,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-2615 Powers and duties of Council; compensation for consultants.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2615.md","filePath":"vacode/2.2-2615.md"}'),a={name:"vacode/2.2-2615.md"},n=t('<h1 id="§-2-2-2615-powers-and-duties-of-council-compensation-for-consultants" tabindex="-1">§ 2.2-2615 Powers and duties of Council; compensation for consultants. <a class="header-anchor" href="#§-2-2-2615-powers-and-duties-of-council-compensation-for-consultants" aria-label="Permalink to &quot;§ 2.2-2615 Powers and duties of Council; compensation for consultants.&quot;">​</a></h1><p>A. The Council shall have the following powers and duties:</p><ol><li><p>Promote a greater understanding and awareness of the history and significance of the Executive Mansion;</p></li><li><p>Take the leadership in guiding the development of research and publications on the history of the Executive Mansion, thus establishing a continuity of effort in this area;</p></li><li><p>Encourage, approve, and accept contributions and bequests and gifts or loans of furniture, works of art, memorabilia, and other property for its use in carrying out the purposes of this article;</p></li><li><p>Purchase appropriate period furnishings and works of art for the Executive Mansion, and exchange or sell property, tangible or intangible, which has been acquired by the Council through gifts or otherwise from the Commonwealth or other public or private organizations, associations, or individuals;</p></li><li><p>Acquire or provide for accession and replacement of objects for the Executive Mansion, either directly or through the Virginia Museum of Fine Arts;</p></li><li><p>Administer all funds, public and private, made available to the Council and to disburse such funds in accordance with the purposes of this article.</p></li></ol><p>B. The Council may employ and fix the compensation of researchers, writers, curators, and other such consultants and professional personnel as it may deem necessary to assist in the exercise and performance of its duties and powers.</p><p>C. Purchases, exchanges, gifts and sales by the Council shall be exempt from the requirements of the Virginia Public Procurement Act. The Mansion Director and/or the Department of General Services shall assist the Council in keeping record of all such transactions.</p><p>1973, c. 431, §§ 9-84.7, 9-45.8; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0392" target="_blank" rel="noreferrer">392</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0409" target="_blank" rel="noreferrer">409</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',6),r=[n];function s(c,l,h,p,u,d){return o(),i("div",null,r)}const m=e(a,[["render",s]]);export{g as __pageData,m as default};
