import{_ as e,o as t,c as i,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-2618 Powers and duties of Council.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2618.md","filePath":"vacode/2.2-2618.md"}'),a={name:"vacode/2.2-2618.md"},o=n('<h1 id="§-2-2-2618-powers-and-duties-of-council" tabindex="-1">§ 2.2-2618 Powers and duties of Council. <a class="header-anchor" href="#§-2-2-2618-powers-and-duties-of-council" aria-label="Permalink to &quot;§ 2.2-2618 Powers and duties of Council.&quot;">​</a></h1><p>The Council, in order to (i) strengthen the criminal justice system in the Commonwealth; (ii) provide a professional organization for the education, training, service and coordination of technical efforts of state prosecutors; and (iii) maintain and improve prosecutor efficiency and effectiveness in enforcing the law of the Commonwealth, shall have, but are not limited to, the following powers and duties:</p><ol><li><p>Organize, supervise and perform functions consistent with this article;</p></li><li><p>Coordinate training and continuing legal education activities for attorneys for the Commonwealth;</p></li><li><p>Contract or enter into agreements with state or federal agencies or education institutions;</p></li><li><p>Gather and disseminate information to attorneys for the Commonwealth relative to their official duties, including changes in the law affecting their office and information on individuals identified as criminal gang members, as transmitted by the Department of Corrections and the Department of Juvenile Justice;</p></li><li><p>Coordinate with the Department of Criminal Justice Services and the Judicial Conference in reference to training and interdisciplinary criminal justice matters;</p></li><li><p>Obtain statistical reports from attorneys for the Commonwealth relating to their performance, function and work-load;</p></li><li><p>Receive and establish an equitable distribution plan for the allocation of any funds from public or private sources;</p></li><li><p>Maintain close contact with the office of the Attorney General and with all attorneys for the Commonwealth and assistant attorneys for the Commonwealth in the discussion of problems or recommendations concerning necessary research, minimum standards, educational needs and other matters relative to upgrading the professional status of attorneys for the Commonwealth;</p></li><li><p>Gather information on changes in the law that affect the duties and responsibilities of law-enforcement officers, make such information available to law-enforcement agencies, law-enforcement training academies, and the Department of Criminal Justice Services; and</p></li><li><p>Perform such other acts as may be necessary for the effective performance of its duties.</p></li></ol><p>1978, c. 455, § 2.1-64.28:4; 1984, c. 779; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0431" target="_blank" rel="noreferrer">431</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0500" target="_blank" rel="noreferrer">500</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0079" target="_blank" rel="noreferrer">79</a>.</p>',4),r=[o];function s(l,c,f,d,h,m){return t(),i("div",null,r)}const g=e(a,[["render",s]]);export{u as __pageData,g as default};
