import{_ as e,o as i,c as a,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-2666.3 (Contingent expiration) Oceana/Fentress Military Advisory Council created; composition; duties; staff support.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2666.3.md","filePath":"vacode/2.2-2666.3.md"}'),r={name:"vacode/2.2-2666.3.md"},n=t('<h1 id="§-2-2-2666-3-contingent-expiration-oceana-fentress-military-advisory-council-created-composition-duties-staff-support" tabindex="-1">§ 2.2-2666.3 (Contingent expiration) Oceana/Fentress Military Advisory Council created; composition; duties; staff support. <a class="header-anchor" href="#§-2-2-2666-3-contingent-expiration-oceana-fentress-military-advisory-council-created-composition-duties-staff-support" aria-label="Permalink to &quot;§ 2.2-2666.3 (Contingent expiration) Oceana/Fentress Military Advisory Council created; composition; duties; staff support.&quot;">​</a></h1><p>A. The Oceana/Fentress Military Advisory Council (the Oceana/Fentress Council) is hereby created as a subunit of the Virginia Military Advisory Council. The Oceana/Fentress Council shall be composed of two members of the Chesapeake City Council, two members of the Virginia Beach City Council, those members of the Virginia General Assembly whose districts encompass Naval Air Station Oceana and Naval Auxiliary Landing Field Fentress, the Commander, Navy Mid-Atlantic Region or his representative, and the Commanding Officer of Naval Air Station Oceana or his representative.</p><p>B. The Oceana/Fentress Council shall identify and study and provide advice and comments to the Virginia Military Advisory Council on issues of mutual concern to the Commonwealth and the Navy concerning Naval Air Station Oceana and Naval Auxiliary Landing Field Fentress and address such other issues as the Governor or the Virginia Military Advisory Council may determine to be appropriate subjects of consideration.</p><p>C. Such staff support as is necessary for the conduct of the Oceana/Fentress Council&#39;s business shall be furnished by the Office of the Secretary of Veterans and Defense Affairs.</p><p>2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0266" target="_blank" rel="noreferrer">266</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0328" target="_blank" rel="noreferrer">328</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0075" target="_blank" rel="noreferrer">75</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0780" target="_blank" rel="noreferrer">780</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0858" target="_blank" rel="noreferrer">858</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0490" target="_blank" rel="noreferrer">490</a>.</p>',5),o=[n];function s(c,l,f,p,d,h){return i(),a("div",null,o)}const v=e(r,[["render",s]]);export{u as __pageData,v as default};
