import{_ as e,o as a,c as r,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2682 Powers and duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2682.md","filePath":"vacode/2.2-2682.md"}'),t={name:"vacode/2.2-2682.md"},i=n('<h1 id="§-2-2-2682-powers-and-duties" tabindex="-1">§ 2.2-2682 Powers and duties. <a class="header-anchor" href="#§-2-2-2682-powers-and-duties" aria-label="Permalink to &quot;§ 2.2-2682 Powers and duties.&quot;">​</a></h1><p>A. The Council shall have the following powers and duties:</p><ol><li><p>Advise the Department of Veterans Services and the General Assembly regarding (i) methods of providing support for ongoing veterans services and programs, and (ii) addressing veterans issues on an ongoing basis;</p></li><li><p>Recommend issues that may potentially impact veterans of the armed forces of the United States and their eligible spouses, orphans, and dependents;</p></li><li><p>Advise the Department of Veterans Services and the Board of Veterans Services on matters of concern to Virginia-domiciled veterans and their eligible spouses, orphans, and dependents;</p></li><li><p>Promote and support existing veterans services and programs;</p></li><li><p>Recommend and promote implementation of new efficient and effective administrative initiatives that enhance existing veterans services and programs or provide for necessary veterans services and programs not currently provided; and</p></li><li><p>Maintain a nonpartisan approach to maintaining and improving veterans services and programs in the Commonwealth.</p></li></ol><p>B. The chairman shall report to the Commissioner and the Board of Veterans Services the results of its meetings and submit an annual report on or before November 30 of each year.</p><p>C. The Council may apply for funds from the Veterans Services Foundation to enable it to better carry out its objectives. The Council shall not impose unreasonable burdens or costs in connection with requests of agencies.</p><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0467" target="_blank" rel="noreferrer">467</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0768" target="_blank" rel="noreferrer">768</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0809" target="_blank" rel="noreferrer">809</a>.</p>',6),o=[i];function s(l,p,d,c,h,g){return a(),r("div",null,o)}const v=e(t,[["render",s]]);export{m as __pageData,v as default};
