import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-2699.11 Compensation; expenses; staffing.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2699.11.md","filePath":"vacode/2.2-2699.11.md"}'),o={name:"vacode/2.2-2699.11.md"},s=r('<h1 id="§-2-2-2699-11-compensation-expenses-staffing" tabindex="-1">§ 2.2-2699.11 Compensation; expenses; staffing. <a class="header-anchor" href="#§-2-2-2699-11-compensation-expenses-staffing" aria-label="Permalink to &quot;§ 2.2-2699.11 Compensation; expenses; staffing.&quot;">​</a></h1><p>A. Members of the Council shall receive no compensation for their services but shall be reimbursed for all reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="/vacode/2.2-2813/">2.2-2813</a> and <a href="/vacode/2.2-2825/">2.2-2825</a>. Funding for the costs of expenses of the members shall be provided by the Secretary of Natural and Historic Resources.</p><p>B. The Office of the Governor and the Secretary of Natural and Historic Resources shall provide staff support to the Council. All agencies of the Commonwealth shall provide assistance to the Council, upon request.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0113" target="_blank" rel="noreferrer">113</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1274" target="_blank" rel="noreferrer">1274</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0401" target="_blank" rel="noreferrer">401</a>.</p>',4),n=[s];function i(c,l,f,p,h,d){return a(),t("div",null,n)}const g=e(o,[["render",i]]);export{u as __pageData,g as default};
