import{_ as e,o as t,c as n,O as i}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-2699.2 Powers and duties of the Council.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2699.2.md","filePath":"vacode/2.2-2699.2.md"}'),o={name:"vacode/2.2-2699.2.md"},a=i('<h1 id="§-2-2-2699-2-powers-and-duties-of-the-council" tabindex="-1">§ 2.2-2699.2 Powers and duties of the Council. <a class="header-anchor" href="#§-2-2-2699-2-powers-and-duties-of-the-council" aria-label="Permalink to &quot;§ 2.2-2699.2 Powers and duties of the Council.&quot;">​</a></h1><p>The Council shall have the power and duty to:</p><ol><li><p>Identify opportunities and recommend actions to use the economic development engine offered by Virginia&#39;s aerospace sector to benefit the sector and the Commonwealth, including the attraction to Virginia of launch and other aerospace companies, as well as federal, national, and international investments, such as the FAA&#39;s NextGen initiative and emerging NASA and other federal programs;</p></li><li><p>Develop a long-term strategic plan to make the Mid-Atlantic Regional Spaceport the commercial hub for space travel originating or concluding in the United States;</p></li><li><p>Contribute to the continued development of the Mid-Atlantic Regional Spaceport. Development efforts shall include, in part:</p></li></ol><p>a. Identification of any federal or state regulatory impediments, including taxation, to the development of the Mid-Atlantic Regional Spaceport;</p><p>b. Identification of threats to the spaceport&#39;s viability, such as encroachment, zoning, mineral exploration and exploitation, and noncompatible uses of the spaceport; and</p><p>c. Identification and recommendation of policy and legislative solutions to potential state legal barriers to human spaceflight;</p><ol start="4"><li><p>Advise the Governor and the General Assembly on infrastructure and marketing investments needed to achieve the full potential of Virginia&#39;s aerospace sector as a whole, including, but not limited to, the Mid-Atlantic Regional Spaceport;</p></li><li><p>Identify and recommend policies to support the critical role of baccalaureate institutions of higher education in the Commonwealth in providing human capital and research contributions that significantly impact the economic development of aerospace-related and aerodynamic-dependent industries in the Commonwealth;</p></li><li><p>Identify and recommend policies to support aerospace sector needs for workforce development as provided by the Virginia Community College System and precollege educational system, including suggestions for enhanced development of Virginia&#39;s high-tech workforce pipeline in engineering, technology, and science; and</p></li><li><p>Assist the Governor in any aerospace-related events and conferences hosted by the Commonwealth.</p></li></ol><p>2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0891" target="_blank" rel="noreferrer">891</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0339" target="_blank" rel="noreferrer">339</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0836" target="_blank" rel="noreferrer">836</a>.</p>',8),r=[a];function l(c,s,d,p,h,m){return t(),n("div",null,r)}const u=e(o,[["render",l]]);export{g as __pageData,u as default};
