import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-2699.4 Powers and duties of the Council.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2699.4.md","filePath":"vacode/2.2-2699.4.md"}'),n={name:"vacode/2.2-2699.4.md"},i=o('<h1 id="§-2-2-2699-4-powers-and-duties-of-the-council" tabindex="-1">§ 2.2-2699.4 Powers and duties of the Council. <a class="header-anchor" href="#§-2-2-2699-4-powers-and-duties-of-the-council" aria-label="Permalink to &quot;§ 2.2-2699.4 Powers and duties of the Council.&quot;">​</a></h1><p>The Council shall have the power and duty to:</p><ol><li><p>Monitor the broadband-based development efforts of other states and nations in areas such as business, education, and health;</p></li><li><p>Advise the Governor, the Secretary of Commerce and Trade, and the General Assembly on policies and strategies related to making affordable broadband services available to every Virginia home and business;</p></li><li><p>Monitor broadband-related activities at the federal level;</p></li><li><p>Encourage public-private partnerships to increase the deployment and adoption of broadband services and applications;</p></li><li><p>Annually report to the Governor and the Joint Commission on Technology and Science on the progress towards the goal of universal access for businesses and on the assessment of Commonwealth broadband infrastructure investments and utilization of Council-supported resources to promote broadband access;</p></li><li><p>Periodically review and comment on the quality, availability, and accessibility of state-maintained or funded broadband resources and programs, including but not limited to: Virginia Resources Authority Act funding of the &quot;Online Community Toolkit&quot;; the Center for Innovative Technology&#39;s mapping and outreach initiatives; investments made through programs administered by the Department of Education, Department of Housing and Community Development, Department of Public Rail and Transportation, and the Tobacco Region Revitalization Commission; and</p></li><li><p>Monitor regulatory and policy changes for potential impact on broadband deployment and sustainability in the Commonwealth.</p></li></ol><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0818" target="_blank" rel="noreferrer">818</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0852" target="_blank" rel="noreferrer">852</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0738" target="_blank" rel="noreferrer">738</a>.</p>',4),r=[i];function s(l,d,c,p,h,u){return a(),t("div",null,r)}const b=e(n,[["render",s]]);export{f as __pageData,b as default};
