import{_ as e,o as t,c as n,O as i}from"./chunks/framework.9482e208.js";const h=JSON.parse('{"title":"§ 2.2-2699.8 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2699.8.md","filePath":"vacode/2.2-2699.8.md"}'),a={name:"vacode/2.2-2699.8.md"},o=i('<h1 id="§-2-2-2699-8-definitions" tabindex="-1">§ 2.2-2699.8 Definitions. <a class="header-anchor" href="#§-2-2-2699-8-definitions" aria-label="Permalink to &quot;§ 2.2-2699.8 Definitions.&quot;">​</a></h1><p>For purposes of this article, unless the context requires a different meaning:</p><p>&quot;Council&quot; means the Virginia Council on Environmental Justice established pursuant to this article.</p><p>&quot;Environmental justice&quot; means the fair treatment and meaningful involvement of all people regardless of race, color, faith, disability, national origin, or income, regarding the development, implementation, or enforcement of any environmental law, regulation, or policy.</p><p>&quot;Fair treatment&quot; means the equitable consideration of all people whereby no group of people bears a disproportionate share of any negative environmental consequence resulting from an industrial, governmental, or commercial operation, program, or policy.</p><p>&quot;Meaningful involvement&quot; means the requirements that (i) affected and vulnerable community residents have access and opportunities to participate in the full cycle of the decision-making process about a proposed activity that will affect their environment or health and (ii) decision-makers will seek out and consider such participation, allowing the views and perspectives of community residents to shape and influence the decision.</p><p>&quot;Resilience&quot; means, as it pertains to climate change, the ability to anticipate, prepare for, and adapt to changing conditions and to withstand, respond to, and recover rapidly from disruptions through adaptable planning and climate solutions.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0113" target="_blank" rel="noreferrer">113</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1274" target="_blank" rel="noreferrer">1274</a>.</p>',8),r=[o];function s(l,c,p,d,u,m){return t(),n("div",null,r)}const g=e(a,[["render",s]]);export{h as __pageData,g as default};
