import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 2.2-2712 Debt Capacity Advisory Committee; membership; terms; chairman; compensation; staff.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2712.md","filePath":"vacode/2.2-2712.md"}'),r={name:"vacode/2.2-2712.md"},o=i('<h1 id="§-2-2-2712-debt-capacity-advisory-committee-membership-terms-chairman-compensation-staff" tabindex="-1">§ 2.2-2712 Debt Capacity Advisory Committee; membership; terms; chairman; compensation; staff. <a class="header-anchor" href="#§-2-2-2712-debt-capacity-advisory-committee-membership-terms-chairman-compensation-staff" aria-label="Permalink to &quot;§ 2.2-2712 Debt Capacity Advisory Committee; membership; terms; chairman; compensation; staff.&quot;">​</a></h1><p>A. The Debt Capacity Advisory Committee (the Committee) is established as an advisory committee, within the meaning of § <a href="/vacode/2.2-2100/">2.2-2100</a>, in the executive branch of state government.</p><p>B. The Committee shall consist of the Secretary of Finance; the State Treasurer; the Director of the Department of Planning and Budget; State Comptroller; the Auditor of Public Accounts; the Director of the Joint Legislative Audit and Review Commission; the Staff Director of the House Committee on Appropriations; the Staff Director of the Senate Committee on Finance and Appropriations; and two citizen members who have expertise in financial matters to be appointed by the Governor.</p><p>C. Of the citizen members appointed for terms beginning July 1, 1994, one shall be appointed for an initial term of three years and the other for an initial term of five years. Successors shall be appointed to serve for terms of four years each. Vacancies occurring other than by expiration of term shall be filled by appointment of the Governor for the remainder of the unexpired term. All appointments shall be subject to confirmation by the General Assembly. Members shall continue to hold office until their successors have been appointed and qualified.</p><p>D. The Secretary of Finance shall be the chairperson of the Committee.</p><p>E. All members of the Committee shall serve without compensation but shall receive reasonable and necessary expenses incurred in the discharge of their duties as provided in § <a href="/vacode/2.2-2825/">2.2-2825</a>.</p><p>F. The Department of the Treasury shall serve as staff to the Committee.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0043" target="_blank" rel="noreferrer">43</a>, § 2.1-304.2; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0032" target="_blank" rel="noreferrer">32</a>.</p>',8),n=[o];function s(c,m,h,p,f,l){return t(),a("div",null,n)}const v=e(r,[["render",s]]);export{b as __pageData,v as default};
