import{_ as e,o as a,c as r,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2722 Compensation; expenses.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2722.md","filePath":"vacode/2.2-2722.md"}'),t={name:"vacode/2.2-2722.md"},o=n('<h1 id="§-2-2-2722-compensation-expenses" tabindex="-1">§ 2.2-2722 Compensation; expenses. <a class="header-anchor" href="#§-2-2-2722-compensation-expenses" aria-label="Permalink to &quot;§ 2.2-2722 Compensation; expenses.&quot;">​</a></h1><p>Legislative members of the Board shall receive such compensation as provided in § <a href="http://law.lis.virginia.gov/vacode/30-19.12/" target="_blank" rel="noreferrer">30-19.12</a>, and nonlegislative citizen members shall receive such compensation for the performance of their duties as provided in § <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a>. All members shall be reimbursed for reasonable and necessary expenses incurred in the performance of their duties as provided in §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2813/" target="_blank" rel="noreferrer">2.2-2813</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>. Funding for the costs of compensation and expenses of the members shall be provided from such funds as may be available to the Center.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0938" target="_blank" rel="noreferrer">938</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0964" target="_blank" rel="noreferrer">964</a>.</p>',3),i=[o];function s(l,c,p,d,h,f){return a(),r("div",null,i)}const v=e(t,[["render",s]]);export{m as __pageData,v as default};
