import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2740 Powers and duties of the Corporation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2740.md","filePath":"vacode/2.2-2740.md"}'),n={name:"vacode/2.2-2740.md"},a=i('<h1 id="§-2-2-2740-powers-and-duties-of-the-corporation" tabindex="-1">§ 2.2-2740 Powers and duties of the Corporation. <a class="header-anchor" href="#§-2-2-2740-powers-and-duties-of-the-corporation" aria-label="Permalink to &quot;§ 2.2-2740 Powers and duties of the Corporation.&quot;">​</a></h1><p>The Corporation shall have the power and duty to:</p><ol><li><p>Periodically assess (i) product and services promotion activities with the Virginia Economic Development Partnership Authority and the Department of Agriculture and Consumer Services and (ii) suggestions from relevant industries on ways to increase exports of Virginia products;</p></li><li><p>Ensure the preparation and execution of effective international trade development marketing and promotional programs, inclusive of both international export and international import programs when economic benefit accrues to Virginia&#39;s economy and businesses;</p></li><li><p>Make available to businesses across the Commonwealth, in conjunction and cooperation with business trade associations, chambers of commerce, universities, and other public and private groups, international trade development programs and services;</p></li><li><p>Encourage and solicit private sector involvement, support, and funding for international trade development in the Commonwealth;</p></li><li><p>Encourage the coordination of international trade development efforts of public institutions, business associations, chambers of commerce, and private industry and collect and maintain data on the development and utilization of international trade development capabilities;</p></li><li><p>Offer a program for the issuance of international documentation for companies located in the Commonwealth if no federal agency or other regulatory body or issuing entity will provide international documentation in a form deemed necessary for international commerce;</p></li><li><p>Adopt, amend, and repeal bylaws, rules, and regulations, not inconsistent with this article, for the administration and regulation of its affairs, to carry into effect the powers and purposes of the Corporation, and for the conduct of its business;</p></li><li><p>Maintain an office at any place within or without the Commonwealth that it designates;</p></li><li><p>Make and execute contracts and all other instruments and agreements necessary or convenient for the performance of its duties and the exercise of its owners and functions under this article;</p></li><li><p>Employ officers, employees, agents, advisers, and consultants, including without limitation financial advisers and other technical advisers and public accountants, and, the provisions of any other law to the contrary notwithstanding, to determine their duties and compensation without the approval of any other agency or instrumentality;</p></li><li><p>Sell, convey, mortgage, pledge, lease, exchange, transfer, and otherwise dispose of all or any part of its properties and assets;</p></li><li><p>Procure insurance, in amounts and from insurers of its choice, or provide self-insurance, against any loss, cost, or expense in connection with its property, assets, or activities, including insurance or self-insurance against liability for its acts or the acts of its directors, employees, or agents and for the indemnification of the members of its Board and its employees and agents;</p></li><li><p>Establish and revise, amend and repeal, and charge and collect fees and charges in connection with any activities or services of the Corporation;</p></li><li><p>Make grants with any funds of the Corporation available for this purpose;</p></li><li><p>Develop policies and procedures generally applicable to the procurement of goods, services, and construction based on competitive principles;</p></li><li><p>Raise money in the corporate, nonprofit, and nonstate communities to finance the Corporation&#39;s activities;</p></li><li><p>Receive and accept from any source aid, grants, and contributions of money, property, labor, or other things of value to be held, used, and applied to carry out the purposes of this article subject to the conditions upon which the aid, grants, or contributions are made;</p></li><li><p>Enter into agreements with any department, agency, or instrumentality of the United States, the Commonwealth, the District of Columbia, or any state for purposes consistent with its mission;</p></li><li><p>Maintain accounts and records as prescribed by the Auditor of Public Accounts, who shall annually audit the accounts of the Corporation; and</p></li><li><p>Do any acts necessary or convenient to the exercise of the powers granted or reasonably implied by this article and not otherwise inconsistent with state law.</p></li></ol><p>2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0749" target="_blank" rel="noreferrer">749</a>.</p>',4),r=[a];function s(c,l,p,d,h,u){return t(),o("div",null,r)}const g=e(n,[["render",s]]);export{m as __pageData,g as default};
