import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-2746 Program Advisory Committee; membership; qualifications; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2746.md","filePath":"vacode/2.2-2746.md"}'),o={name:"vacode/2.2-2746.md"},r=i('<h1 id="§-2-2-2746-program-advisory-committee-membership-qualifications-duties" tabindex="-1">§ 2.2-2746 Program Advisory Committee; membership; qualifications; duties. <a class="header-anchor" href="#§-2-2-2746-program-advisory-committee-membership-qualifications-duties" aria-label="Permalink to &quot;§ 2.2-2746 Program Advisory Committee; membership; qualifications; duties.&quot;">​</a></h1><p>A. In order to assist the Board in fulfilling its duties under § <a href="/vacode/23.1-704/">23.1-704</a> and this chapter and to assist the Plan&#39;s chief executive officer in directing, managing, and administering the Program, the Board shall appoint the Program Advisory Committee to provide sophisticated, objective, and prudent administrative and investment advice and direction, as requested by the Board. The Committee may develop Program recommendations for the Board and perform such other duties as the Board may delegate to the Committee.</p><p>B. The Board shall develop requirements, procedures, and guidelines regarding Committee membership.</p><p>C. Members of the Committee shall demonstrate extensive experience in one or more of the following areas: retirement plan design, retirement plan investments, domestic or international equity or fixed-income securities, cash management, alternative investments, institutional real estate investments, or managed futures.</p><p>D. Members of the Committee shall serve at the pleasure of the Board and may be removed by a majority vote of the Board.</p><p>E. Members of the Committee shall receive no compensation but shall be reimbursed for actual expenses incurred in the performance of their duties.</p><p>F. The recommendations of the Committee shall not be binding upon the Board.</p><p>G. The disclosure requirements of subsection B of § <a href="/vacode/2.2-3114/">2.2-3114</a> shall apply to each member of the Committee who is not also a Board member.</p><p>H. The Board may appoint such other advisory committees as it deems necessary and shall set the qualifications for members of any such advisory committee by resolution.</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0556" target="_blank" rel="noreferrer">556</a>.</p>',10),s=[r];function n(m,d,h,c,l,p){return t(),a("div",null,s)}const v=e(o,[["render",n]]);export{f as __pageData,v as default};
