import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-2812.1 State agencies prohibited from inquiring about arrests, charges, or convictions on employment applications; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2812.1.md","filePath":"vacode/2.2-2812.1.md"}'),a={name:"vacode/2.2-2812.1.md"},n=o('<h1 id="§-2-2-2812-1-state-agencies-prohibited-from-inquiring-about-arrests-charges-or-convictions-on-employment-applications-exceptions" tabindex="-1">§ 2.2-2812.1 State agencies prohibited from inquiring about arrests, charges, or convictions on employment applications; exceptions. <a class="header-anchor" href="#§-2-2-2812-1-state-agencies-prohibited-from-inquiring-about-arrests-charges-or-convictions-on-employment-applications-exceptions" aria-label="Permalink to &quot;§ 2.2-2812.1 State agencies prohibited from inquiring about arrests, charges, or convictions on employment applications; exceptions.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Conviction&quot; means any adjudication that an individual committed a crime, any finding of guilt after a criminal trial by a court of competent jurisdiction, or any plea of guilty or nolo contendere to a criminal charge.</p><p>&quot;Staff interview&quot; means any interview of a prospective employee for a job by current state agency staff.</p><p>&quot;State agency&quot; means any authority, board, department, instrumentality, institution, agency, or other unit of state government.</p><p>B. No state agency shall request a prospective employee to complete an application for employment that includes a question inquiring whether the prospective employee has ever been arrested for, charged with, or convicted of any crime. This prohibition shall not apply to any employment-related applications or questionnaires provided during or after a staff interview.</p><p>C. No state agency shall inquire whether a prospective employee has ever been arrested for, charged with, or convicted of any crime unless the inquiry takes place during or after a staff interview of the prospective employee.</p><p>D. Nothing in this section shall prevent a state agency from considering information received during or after a staff interview pertaining to a prospective employee having been arrested for, charged with, or convicted of any crime.</p><p>E. The prohibition in this section shall not apply to positions designated as sensitive pursuant to § <a href="/vacode/2.2-1201.1/">2.2-1201.1</a>, to law-enforcement agency positions or positions related to law-enforcement agencies, or to state agencies that are expressly permitted to inquire into an individual&#39;s criminal arrests or charges for employment purposes pursuant to any provision of federal or state law.</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0422" target="_blank" rel="noreferrer">422</a>.</p>',10),r=[n];function s(c,p,l,d,h,m){return t(),i("div",null,r)}const g=e(a,[["render",s]]);export{u as __pageData,g as default};
