import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 2.2-2818.1 Supplemental health insurance coverage; state employees eligible for military health insurance coverage.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2818.1.md","filePath":"vacode/2.2-2818.1.md"}'),l={name:"vacode/2.2-2818.1.md"},n=r('<h1 id="§-2-2-2818-1-supplemental-health-insurance-coverage-state-employees-eligible-for-military-health-insurance-coverage" tabindex="-1">§ 2.2-2818.1 Supplemental health insurance coverage; state employees eligible for military health insurance coverage. <a class="header-anchor" href="#§-2-2-2818-1-supplemental-health-insurance-coverage-state-employees-eligible-for-military-health-insurance-coverage" aria-label="Permalink to &quot;§ 2.2-2818.1 Supplemental health insurance coverage; state employees eligible for military health insurance coverage.&quot;">​</a></h1><p>A. The Department of Human Resource Management may offer a voluntary supplemental health coverage program for state employees under this section.</p><p>B. Under the supplemental health coverage program, a state employee who is eligible to participate in the health insurance program pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a> and who is also eligible for benefits under the TRICARE Military Health System as a military retiree may elect to receive coverage under a TRICARE supplemental health plan offered as an option under the state&#39;s cafeteria plan established under § 125 of the Internal Revenue Code. Dependents eligible to participate in the state health insurance program who are also TRICARE eligible may also be covered under the TRICARE supplemental health plan.</p><p>C. The cost of supplemental health coverage provided under this section shall be paid in full by the member on a pre-tax basis subject to the rules and regulations of § 125 of the Internal Revenue Code.</p><p>D. The Department of Human Resource Management may not implement a supplemental health coverage program under this section if the Department finds that the program would not be cost-effective or would otherwise not be advantageous to the state or program participants.</p><p>2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0093" target="_blank" rel="noreferrer">93</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0035" target="_blank" rel="noreferrer">35</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0045" target="_blank" rel="noreferrer">45</a>.</p>',6),o=[n];function i(s,p,h,c,m,u){return a(),t("div",null,o)}const f=e(l,[["render",i]]);export{d as __pageData,f as default};
