import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2818.2 Application of mandates to the state employee health insurance plan.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2818.2.md","filePath":"vacode/2.2-2818.2.md"}'),n={name:"vacode/2.2-2818.2.md"},o=r('<h1 id="§-2-2-2818-2-application-of-mandates-to-the-state-employee-health-insurance-plan" tabindex="-1">§ 2.2-2818.2 Application of mandates to the state employee health insurance plan. <a class="header-anchor" href="#§-2-2-2818-2-application-of-mandates-to-the-state-employee-health-insurance-plan" aria-label="Permalink to &quot;§ 2.2-2818.2 Application of mandates to the state employee health insurance plan.&quot;">​</a></h1><p>A. As used in this section, &quot;insurance mandate&quot; means a mandatory obligation with respect to coverage, benefits, or the number or types of providers imposed on policies of accident and health insurance under Title 38.2. &quot;Insurance mandate&quot; does not include (i) an administrative rule or regulation imposing a mandatory obligation with respect to coverage, benefits, or providers unless that mandatory obligation was specifically imposed on policies of accident and health insurance by statute or (ii) any obligation imposed on a health carrier by § <a href="http://law.lis.virginia.gov/vacode/38.2-3407.5:2/" target="_blank" rel="noreferrer">38.2-3407.5:2</a>.</p><p>B. Notwithstanding the provisions of § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a>, any law imposed under Title 38.2 that becomes effective on or after July 1, 2009, that provides for an insurance mandate for policies of accident and health insurance shall also apply to health coverage offered to state employees pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a>.</p><p>C. If health coverage offered to state employees under § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a> offers coverage in the same manner and to the same extent as the coverage required by an insurance mandate imposed under Title 38.2 or coverage that is greater than an insurance mandate imposed under Title 38.2, the coverage offered to state employees under § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a> shall be considered in compliance with the insurance mandate.</p><p>2009, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0247" target="_blank" rel="noreferrer">247</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0317" target="_blank" rel="noreferrer">317</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0716" target="_blank" rel="noreferrer">716</a>.</p>',5),i=[o];function s(l,c,p,h,d,f){return a(),t("div",null,i)}const u=e(n,[["render",s]]);export{m as __pageData,u as default};
