import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-2820 Purchase of health insurance coverage by part-time state employees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2820.md","filePath":"vacode/2.2-2820.md"}'),n={name:"vacode/2.2-2820.md"},i=r('<h1 id="§-2-2-2820-purchase-of-health-insurance-coverage-by-part-time-state-employees" tabindex="-1">§ 2.2-2820 Purchase of health insurance coverage by part-time state employees. <a class="header-anchor" href="#§-2-2-2820-purchase-of-health-insurance-coverage-by-part-time-state-employees" aria-label="Permalink to &quot;§ 2.2-2820 Purchase of health insurance coverage by part-time state employees.&quot;">​</a></h1><p>A. Any part-time state employee employed by the Commonwealth and working twenty or more hours per week for a period of at least six months may, upon proper application to the Department of Human Resource Management (the Department), to purchase health insurance coverage for himself through a health insurance plan administered by the Department. This plan for part-time employees may differ from the other plans sponsored by the Department for state employees and shall be exempt from all mandates contained in § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a>.</p><p>B. Applications to purchase health insurance coverage under this section shall be made on an application form prescribed by the Department. In addition to his application, the applicant shall provide any necessary supporting documents requested by the Department.</p><p>C. Upon payment of the required premiums, coverage shall be effective retroactive to the date of the application.</p><p>D. The terms, conditions, and costs of health insurance coverage purchased under this section shall be subject to administration by the Department. The Department may increase the cost of coverage consistent with its administration of the health insurance plans under § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a>.</p><p>E. Health insurance coverage purchased under this section shall automatically terminate upon the occurrence of any of the following: (i) the applicant&#39;s death, (ii) alternate health insurance coverage being obtained by the applicant, (iii) the applicant&#39;s separation from state service, or (iv) any applicable condition outlined in the policies and procedures of the Department governing its administration of health insurance plans pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-2818/" target="_blank" rel="noreferrer">2.2-2818</a>.</p><p>1999, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>, § 2.1-20.1:06; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0066" target="_blank" rel="noreferrer">66</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',7),o=[i];function s(p,l,c,h,m,d){return a(),t("div",null,o)}const g=e(n,[["render",s]]);export{u as __pageData,g as default};
