import{_ as e,o,c as r,O as i}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 2.2-2821.3 Leave for volunteer members of Civil Air Patrol.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2821.3.md","filePath":"vacode/2.2-2821.3.md"}'),t={name:"vacode/2.2-2821.3.md"},a=i('<h1 id="§-2-2-2821-3-leave-for-volunteer-members-of-civil-air-patrol" tabindex="-1">§ 2.2-2821.3 Leave for volunteer members of Civil Air Patrol. <a class="header-anchor" href="#§-2-2-2821-3-leave-for-volunteer-members-of-civil-air-patrol" aria-label="Permalink to &quot;§ 2.2-2821.3 Leave for volunteer members of Civil Air Patrol.&quot;">​</a></h1><p>A. All officers and employees of the Commonwealth or of any political subdivision of the Commonwealth who are volunteer members of the Civil Air Patrol shall be entitled to leaves of absence from their respective duties without loss of seniority, accrued leave, benefits, or efficiency rating on all days during which such officer or employee is (i) engaged in training for emergency missions with the Civil Air Patrol, not to exceed 10 workdays per federal fiscal year, or (ii) responding to an emergency mission as a Civil Air Patrol volunteer, not to exceed 30 workdays per federal fiscal year.</p><p>B. Any officer or employee requesting leave pursuant to this section shall provide (i) certification that the officer or employee has been authorized by the United States Air Force, the Governor, or a department, division, agency, or political subdivision of the Commonwealth to respond to or train for an emergency mission and (ii) verification from the Civil Air Patrol of the emergency need of the officer&#39;s or employee&#39;s volunteer service.</p><p>C. An employer may treat the officer or employee leaves of absence pursuant to this section as unpaid leave. No employer shall require an employee to exhaust any other leave to which the officer or employee is entitled prior to such leaves of absence. Nothing in this subsection shall be construed to prevent an employer from providing paid leave during such leaves of absence.</p><p>D. Any officer or employee aggrieved by a violation of any provision of this section may bring an action pursuant to the State Grievance Procedure (§ <a href="/vacode/2.2-3000/">2.2-3000</a> et seq.).</p><p>2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0277" target="_blank" rel="noreferrer">277</a>.</p>',6),n=[a];function s(l,c,f,h,p,m){return o(),r("div",null,n)}const u=e(t,[["render",s]]);export{d as __pageData,u as default};
