import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 2.2-2826 Travel expense accounts; review by Comptroller.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2826.md","filePath":"vacode/2.2-2826.md"}'),a={name:"vacode/2.2-2826.md"},n=t('<h1 id="§-2-2-2826-travel-expense-accounts-review-by-comptroller" tabindex="-1">§ 2.2-2826 Travel expense accounts; review by Comptroller. <a class="header-anchor" href="#§-2-2-2826-travel-expense-accounts-review-by-comptroller" aria-label="Permalink to &quot;§ 2.2-2826 Travel expense accounts; review by Comptroller.&quot;">​</a></h1><p>All travel expense accounts shall be submitted on forms prescribed or approved by the Comptroller. Review shall be made by the Comptroller of such accounts subject to the provisions of § <a href="http://law.lis.virginia.gov/vacode/2.2-1822/" target="_blank" rel="noreferrer">2.2-1822</a>. If accounts do not conform to the provisions of § <a href="http://law.lis.virginia.gov/vacode/2.2-2825/" target="_blank" rel="noreferrer">2.2-2825</a>, the Comptroller shall return those accounts to the agency or commission with an explanation of why they do not conform. The agency or commission may correct the accounts and resubmit them to the Comptroller.</p><p>1954, c. 709, § 14-5.5; 1964, c. 386, § 14.1-10; 1977, c. 493, § 14.1-5.3; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>, § 2.1-20.11; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',3),c=[n];function l(s,i,p,h,_,m){return r(),o("div",null,c)}const f=e(a,[["render",l]]);export{d as __pageData,f as default};
