import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 2.2-2831 Payment of severance benefits; exceptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2831.md","filePath":"vacode/2.2-2831.md"}'),o={name:"vacode/2.2-2831.md"},n=i('<h1 id="§-2-2-2831-payment-of-severance-benefits-exceptions" tabindex="-1">§ 2.2-2831 Payment of severance benefits; exceptions. <a class="header-anchor" href="#§-2-2-2831-payment-of-severance-benefits-exceptions" aria-label="Permalink to &quot;§ 2.2-2831 Payment of severance benefits; exceptions.&quot;">​</a></h1><p>No severance benefit shall be provided to any state officer or employee except as specifically provided by law. The provisions of this section shall not apply to any severance benefit provided to (i) any officers or faculty of a public institution of higher education as defined in § <a href="http://law.lis.virginia.gov/vacode/23.1-100/" target="_blank" rel="noreferrer">23.1-100</a> or (ii) a state officer or employee who is not eligible for a transitional severance benefit pursuant to Chapter 32 (§ <a href="http://law.lis.virginia.gov/vacode/2.2-3200/" target="_blank" rel="noreferrer">2.2-3200</a> et seq.) of this title. Nothing in this section shall be construed to prohibit payments in settlement of an employment dispute approved pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-514/" target="_blank" rel="noreferrer">2.2-514</a> or payments in satisfaction of a judgment.</p><p>2006, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0813" target="_blank" rel="noreferrer">813</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0902" target="_blank" rel="noreferrer">902</a>.</p>',3),r=[n];function s(c,l,f,p,d,h){return t(),a("div",null,r)}const g=e(o,[["render",s]]);export{v as __pageData,g as default};
