import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-3103.2 Return of gifts.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3103.2.md","filePath":"vacode/2.2-3103.2.md"}'),a={name:"vacode/2.2-3103.2.md"},r=i('<h1 id="§-2-2-3103-2-return-of-gifts" tabindex="-1">§ 2.2-3103.2 Return of gifts. <a class="header-anchor" href="#§-2-2-3103-2-return-of-gifts" aria-label="Permalink to &quot;§ 2.2-3103.2 Return of gifts.&quot;">​</a></h1><p>No person shall be in violation of any provision of this chapter prohibiting the acceptance of a gift if (i) the gift is not used by such person and the gift or its equivalent in money is returned to the donor or delivered to a charitable organization within a reasonable period of time upon the discovery of the value of the gift and is not claimed as a charitable contribution for federal income tax purposes or (ii) consideration is given by the donee to the donor for the value of the gift within a reasonable period of time upon the discovery of the value of the gift provided that such consideration reduces the value of the gift to an amount not in excess of $100 as provided in subsection B or C of § <a href="http://law.lis.virginia.gov/vacode/2.2-3103.1/" target="_blank" rel="noreferrer">2.2-3103.1</a>.</p><p>2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0763" target="_blank" rel="noreferrer">763</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0777" target="_blank" rel="noreferrer">777</a>.</p>',3),n=[r];function s(f,c,h,d,l,p){return t(),o("div",null,n)}const g=e(a,[["render",s]]);export{u as __pageData,g as default};
