import{_ as o,o as e,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-3104.02 Prohibited conduct for constitutional officers.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3104.02.md","filePath":"vacode/2.2-3104.02.md"}'),n={name:"vacode/2.2-3104.02.md"},a=i('<h1 id="§-2-2-3104-02-prohibited-conduct-for-constitutional-officers" tabindex="-1">§ 2.2-3104.02 Prohibited conduct for constitutional officers. <a class="header-anchor" href="#§-2-2-3104-02-prohibited-conduct-for-constitutional-officers" aria-label="Permalink to &quot;§ 2.2-3104.02 Prohibited conduct for constitutional officers.&quot;">​</a></h1><p>In addition to the prohibitions contained in § <a href="/vacode/2.2-3103/">2.2-3103</a>, no constitutional officer shall, during the one year after the termination of his public service, act in a representative capacity on behalf of any person or group, for compensation, on any matter before the agency of which he was an officer.</p><p>The provisions of this section shall not apply to any attorney for the Commonwealth.</p><p>Any person subject to the provisions of this section may apply to the Council or the attorney for the Commonwealth for the jurisdiction where such person was elected as provided in § <a href="/vacode/2.2-3126/">2.2-3126</a>, for an advisory opinion as to the application of the restriction imposed by this section on any post-public employment position or opportunity.</p><p>2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0591" target="_blank" rel="noreferrer">591</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0111" target="_blank" rel="noreferrer">111</a>.</p>',5),r=[a];function c(s,p,f,h,l,d){return e(),t("div",null,r)}const m=o(n,[["render",c]]);export{u as __pageData,m as default};
