import{_ as e,o,c as t,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-3108 Prohibited contracts by members of school boards.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3108.md","filePath":"vacode/2.2-3108.md"}'),r={name:"vacode/2.2-3108.md"},s=a('<h1 id="§-2-2-3108-prohibited-contracts-by-members-of-school-boards" tabindex="-1">§ 2.2-3108 Prohibited contracts by members of school boards. <a class="header-anchor" href="#§-2-2-3108-prohibited-contracts-by-members-of-school-boards" aria-label="Permalink to &quot;§ 2.2-3108 Prohibited contracts by members of school boards.&quot;">​</a></h1><p>A. No person elected or appointed as a member of a local school board shall have a personal interest in (i) any contract with his school board or (ii) any contract with any governmental agency that is subject to the ultimate control of the school board of which he is a member.</p><p>B. The provisions of this section shall not apply to:</p><ol><li><p>A member&#39;s personal interest in a contract of employment provided the employment first began prior to the member becoming a member of the school board;</p></li><li><p>Contracts for the sale by a governmental agency of services or goods at uniform prices available to the public; or</p></li><li><p>A contract awarded to a member of a school board as a result of competitive sealed bidding where the school board has established a need for the same or substantially similar goods through purchases prior to the election or appointment of the member to serve on the school board. However, the member shall have no involvement in the preparation of the specifications for such contract, and the remaining members of the school board, by written resolution, shall state that it is in the public interest for the member to bid on such contract.</p></li></ol><p>1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0548" target="_blank" rel="noreferrer">548</a>, § 2.1-639.7:1; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',5),i=[s];function n(c,l,h,b,m,d){return o(),t("div",null,i)}const _=e(r,[["render",n]]);export{f as __pageData,_ as default};
