import{_ as e,o,c as r,O as a}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 2.2-3109 Prohibited contracts by other officers and employees of local governmental agencies.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3109.md","filePath":"vacode/2.2-3109.md"}'),t={name:"vacode/2.2-3109.md"},i=a('<h1 id="§-2-2-3109-prohibited-contracts-by-other-officers-and-employees-of-local-governmental-agencies" tabindex="-1">§ 2.2-3109 Prohibited contracts by other officers and employees of local governmental agencies. <a class="header-anchor" href="#§-2-2-3109-prohibited-contracts-by-other-officers-and-employees-of-local-governmental-agencies" aria-label="Permalink to &quot;§ 2.2-3109 Prohibited contracts by other officers and employees of local governmental agencies.&quot;">​</a></h1><p>A. No other officer or employee of any governmental agency of local government, including a hospital authority as defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-3109.1/" target="_blank" rel="noreferrer">2.2-3109.1</a>, shall have a personal interest in a contract with the agency of which he is an officer or employee other than his own contract of employment.</p><p>B. No officer or employee of any governmental agency of local government, including a hospital authority as defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-3109.1/" target="_blank" rel="noreferrer">2.2-3109.1</a>, shall have a personal interest in a contract with any other governmental agency that is a component of the government of his county, city or town unless such contract is (i) awarded as a result of competitive sealed bidding or competitive negotiation as set forth in § <a href="http://law.lis.virginia.gov/vacode/2.2-4302.1/" target="_blank" rel="noreferrer">2.2-4302.1</a> or <a href="http://law.lis.virginia.gov/vacode/2.2-4302.2/" target="_blank" rel="noreferrer">2.2-4302.2</a> or is awarded as a result of a procedure embodying competitive principles as authorized by subdivision A 10 or 11 of § <a href="http://law.lis.virginia.gov/vacode/2.2-4343/" target="_blank" rel="noreferrer">2.2-4343</a> or (ii) is awarded after a finding, in writing, by the administrative head of the governmental agency that competitive bidding or negotiation is contrary to the best interest of the public.</p><p>C. The provisions of this section shall not apply to:</p><ol><li><p>An employee&#39;s personal interest in additional contracts for goods or services, or contracts of employment with his own governmental agency that accrue to him because of a member of his immediate family, provided the employee does not exercise any control over (i) the employment or the employment activities of the member of his immediate family and (ii) the employee is not in a position to influence those activities or the award of the contract for goods or services;</p></li><li><p>An officer&#39;s or employee&#39;s personal interest in a contract of employment with any other governmental agency that is a component part of the government of his county, city or town;</p></li><li><p>Contracts for the sale by a governmental agency of services or goods at uniform prices available to the general public;</p></li><li><p>Members of local governing bodies who are subject to § <a href="http://law.lis.virginia.gov/vacode/2.2-3107/" target="_blank" rel="noreferrer">2.2-3107</a>;</p></li><li><p>Members of local school boards who are subject to § <a href="http://law.lis.virginia.gov/vacode/2.2-3108/" target="_blank" rel="noreferrer">2.2-3108</a>; or</p></li><li><p>Any ownership or financial interest of members of the governing body, administrators, and other personnel serving in a public charter school in renovating, lending, granting, or leasing public charter school facilities, as the case may be, provided such interest has been disclosed in the public charter school application as required by § <a href="http://law.lis.virginia.gov/vacode/22.1-212.8/" target="_blank" rel="noreferrer">22.1-212.8</a>.</p></li></ol><p>1987, Sp. Sess., c. 1, § 2.1-639.8; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0548" target="_blank" rel="noreferrer">548</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0530" target="_blank" rel="noreferrer">530</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0862" target="_blank" rel="noreferrer">862</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0583" target="_blank" rel="noreferrer">583</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0699" target="_blank" rel="noreferrer">699</a>.</p>',6),n=[i];function l(s,c,h,f,g,p){return o(),r("div",null,n)}const m=e(t,[["render",l]]);export{v as __pageData,m as default};
