import{_ as e,o as i,c as r,O as o}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-3118.1 Special provisions for individuals serving in or seeking multiple positions or offices; reappointees.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3118.1.md","filePath":"vacode/2.2-3118.1.md"}'),a={name:"vacode/2.2-3118.1.md"},n=o('<h1 id="§-2-2-3118-1-special-provisions-for-individuals-serving-in-or-seeking-multiple-positions-or-offices-reappointees" tabindex="-1">§ 2.2-3118.1 Special provisions for individuals serving in or seeking multiple positions or offices; reappointees. <a class="header-anchor" href="#§-2-2-3118-1-special-provisions-for-individuals-serving-in-or-seeking-multiple-positions-or-offices-reappointees" aria-label="Permalink to &quot;§ 2.2-3118.1 Special provisions for individuals serving in or seeking multiple positions or offices; reappointees.&quot;">​</a></h1><p>A. The filing of a single current statement of economic interests by an individual required to file the form prescribed in § <a href="/vacode/2.2-3117/">2.2-3117</a> shall suffice for the purposes of this chapter as filing for all positions or offices held or sought by such individual during the course of a calendar year. The filing of a single current financial disclosure statement by an individual required to file the form prescribed in § <a href="/vacode/2.2-3118/">2.2-3118</a> shall suffice for the purposes of this chapter as filing for all positions or offices held or sought by such individual and requiring the filing of the § <a href="/vacode/2.2-3118/">2.2-3118</a> form during the course of a calendar year.</p><p>B. Any individual who has met the requirement for annually filing a statement provided in § <a href="/vacode/2.2-3117/">2.2-3117</a> or <a href="/vacode/2.2-3118/">2.2-3118</a> shall not be required to file an additional statement upon such individual&#39;s reappointment to the same office or position for which he is required to file, provided such reappointment occurs within 12 months after filing such annual statement.</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0397" target="_blank" rel="noreferrer">397</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0792" target="_blank" rel="noreferrer">792</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>; 2016, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0773" target="_blank" rel="noreferrer">773</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0774" target="_blank" rel="noreferrer">774</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0529" target="_blank" rel="noreferrer">529</a>.</p>',4),t=[n];function s(l,f,c,p,d,h){return i(),r("div",null,t)}const v=e(a,[["render",s]]);export{g as __pageData,v as default};
