import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 2.2-3123 Invalidation of contract; recision of sales.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3123.md","filePath":"vacode/2.2-3123.md"}'),t={name:"vacode/2.2-3123.md"},n=o('<h1 id="§-2-2-3123-invalidation-of-contract-recision-of-sales" tabindex="-1">§ 2.2-3123 Invalidation of contract; recision of sales. <a class="header-anchor" href="#§-2-2-3123-invalidation-of-contract-recision-of-sales" aria-label="Permalink to &quot;§ 2.2-3123 Invalidation of contract; recision of sales.&quot;">​</a></h1><p>A. Any contract made in violation of § <a href="http://law.lis.virginia.gov/vacode/2.2-3103/" target="_blank" rel="noreferrer">2.2-3103</a> or §§ <a href="http://law.lis.virginia.gov/vacode/2.2-3106/" target="_blank" rel="noreferrer">2.2-3106</a> through <a href="http://law.lis.virginia.gov/vacode/2.2-3109/" target="_blank" rel="noreferrer">2.2-3109</a> may be declared void and may be rescinded by the governing body of the contracting or selling governmental agency within five years of the date of such contract. In cases in which the contract is invalidated, the contractor shall retain or receive only the reasonable value, with no increment for profit or commission, of the property or services furnished prior to the date of receiving notice that the contract has been voided. In cases of recision of a contract of sale, any refund or restitution shall be made to the contracting or selling governmental agency.</p><p>B. Any purchase by an officer or employee made in violation of § <a href="http://law.lis.virginia.gov/vacode/2.2-3103/" target="_blank" rel="noreferrer">2.2-3103</a> or §§ <a href="http://law.lis.virginia.gov/vacode/2.2-3106/" target="_blank" rel="noreferrer">2.2-3106</a> through <a href="http://law.lis.virginia.gov/vacode/2.2-3109/" target="_blank" rel="noreferrer">2.2-3109</a> may be rescinded by the governing body of the contracting or selling governmental agency within five years of the date of such purchase.</p><p>1987, Sp. Sess., c. 1, § 2.1-639.20; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',4),i=[n];function c(s,l,h,f,d,v){return a(),r("div",null,i)}const p=e(t,[["render",c]]);export{_ as __pageData,p as default};
