import{_ as e,o as a,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-3130 Attendance requirements.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3130.md","filePath":"vacode/2.2-3130.md"}'),n={name:"vacode/2.2-3130.md"},o=r('<h1 id="§-2-2-3130-attendance-requirements" tabindex="-1">§ 2.2-3130 Attendance requirements. <a class="header-anchor" href="#§-2-2-3130-attendance-requirements" aria-label="Permalink to &quot;§ 2.2-3130 Attendance requirements.&quot;">​</a></h1><p>Except as set forth in § <a href="http://law.lis.virginia.gov/vacode/2.2-3131/" target="_blank" rel="noreferrer">2.2-3131</a>, each state filer shall attend the orientation course required in § <a href="http://law.lis.virginia.gov/vacode/2.2-3128/" target="_blank" rel="noreferrer">2.2-3128</a>, as follows:</p><ol><li><p>For a state filer who holds a position with the agency on January 1, 2004, not later than December 31, 2004 and, thereafter, at least once during each consecutive period of two calendar years commencing on January 1, 2006.</p></li><li><p>For a person who becomes a state filer with the agency after January 1, 2004, within two months after he or she becomes a state filer and at least once during each consecutive period of two calendar years commencing on the first odd-numbered year thereafter.</p></li></ol><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0134" target="_blank" rel="noreferrer">134</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0392" target="_blank" rel="noreferrer">392</a>.</p>',4),i=[o];function c(s,l,d,h,_,f){return a(),t("div",null,i)}const u=e(n,[["render",c]]);export{m as __pageData,u as default};
