import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 2.2-3131 Exemptions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3131.md","filePath":"vacode/2.2-3131.md"}'),i={name:"vacode/2.2-3131.md"},n=r('<h1 id="§-2-2-3131-exemptions" tabindex="-1">§ 2.2-3131 Exemptions. <a class="header-anchor" href="#§-2-2-3131-exemptions" aria-label="Permalink to &quot;§ 2.2-3131 Exemptions.&quot;">​</a></h1><p>A. The requirements of § <a href="http://law.lis.virginia.gov/vacode/2.2-3130/" target="_blank" rel="noreferrer">2.2-3130</a> shall not apply to state filers with a state agency who have taken an equivalent ethics orientation course through another state agency within the time periods set forth in subdivision 1 or 2 of § <a href="http://law.lis.virginia.gov/vacode/2.2-3130/" target="_blank" rel="noreferrer">2.2-3130</a>, as applicable.</p><p>B. State agencies may jointly conduct and state filers from more than one state agency may jointly attend an orientation course required by § <a href="http://law.lis.virginia.gov/vacode/2.2-3128/" target="_blank" rel="noreferrer">2.2-3128</a>, as long as the course content is relevant to the official duties of the attending state filers.</p><p>C. Before conducting each orientation course required by § <a href="http://law.lis.virginia.gov/vacode/2.2-3128/" target="_blank" rel="noreferrer">2.2-3128</a>, state agencies shall consult with the Attorney General and the Virginia Conflict of Interest and Ethics Advisory Council regarding appropriate course content.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0134" target="_blank" rel="noreferrer">134</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0392" target="_blank" rel="noreferrer">392</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0792" target="_blank" rel="noreferrer">792</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0804" target="_blank" rel="noreferrer">804</a>.</p>',5),o=[n];function s(l,c,h,g,p,f){return t(),a("div",null,o)}const v=e(i,[["render",s]]);export{_ as __pageData,v as default};
