import{_ as e,o as t,c as o,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-3132 Training on prohibited conduct and conflicts of interest.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3132.md","filePath":"vacode/2.2-3132.md"}'),n={name:"vacode/2.2-3132.md"},a=i('<h1 id="§-2-2-3132-training-on-prohibited-conduct-and-conflicts-of-interest" tabindex="-1">§ 2.2-3132 Training on prohibited conduct and conflicts of interest. <a class="header-anchor" href="#§-2-2-3132-training-on-prohibited-conduct-and-conflicts-of-interest" aria-label="Permalink to &quot;§ 2.2-3132 Training on prohibited conduct and conflicts of interest.&quot;">​</a></h1><p>A. The Council shall provide training sessions for local elected officials and the executive directors and members of industrial development authorities and economic development authorities, as created by the Industrial Development and Revenue Bond Act (§ <a href="/vacode/15.2-4900/">15.2-4900</a> et seq.), on the provisions of the State and Local Government Conflict of Interests Act (§ <a href="/vacode/2.2-3100/">2.2-3100</a> et seq.). The Council may provide such training sessions by online means.</p><p>B. Each local elected official and the executive director and members of each industrial development authority and economic development authority, as created by the Industrial Development and Revenue Bond Act, shall complete the training session described in subsection A within two months after assuming the local elected office and thereafter at least once during each consecutive period of two calendar years while he holds such office, commencing with the date on which he last completed a training session. No penalty shall be imposed on a local elected official or an executive director or member of an industrial development authority or an economic development authority for failing to complete a training session.</p><p>C. The clerk of the respective governing body or school board shall maintain records indicating local elected officials and executive directors and members of industrial development authorities and economic development authorities subject to the training requirement and the dates of their completion of a training session pursuant to subsection B. Such records shall be maintained as public records for five years in the office of the clerk of the respective governing body or school board.</p><p>2019, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?191+ful+CHAP0530" target="_blank" rel="noreferrer">530</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0076" target="_blank" rel="noreferrer">76</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0080" target="_blank" rel="noreferrer">80</a>.</p>',5),r=[a];function c(s,l,d,h,f,p){return t(),o("div",null,r)}const v=e(n,[["render",c]]);export{m as __pageData,v as default};
