import{_ as e,o as a,c as o,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-3301 Acts, business transactions, legal proceedings, etc., on holidays valid.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3301.md","filePath":"vacode/2.2-3301.md"}'),t={name:"vacode/2.2-3301.md"},r=n('<h1 id="§-2-2-3301-acts-business-transactions-legal-proceedings-etc-on-holidays-valid" tabindex="-1">§ 2.2-3301 Acts, business transactions, legal proceedings, etc., on holidays valid. <a class="header-anchor" href="#§-2-2-3301-acts-business-transactions-legal-proceedings-etc-on-holidays-valid" aria-label="Permalink to &quot;§ 2.2-3301 Acts, business transactions, legal proceedings, etc., on holidays valid.&quot;">​</a></h1><p>No contract made, instrument executed, or act done on any of the legal holidays named in § <a href="http://law.lis.virginia.gov/vacode/2.2-3300/" target="_blank" rel="noreferrer">2.2-3300</a> or on any Saturday shall be thereby rendered invalid. Nothing in § <a href="http://law.lis.virginia.gov/vacode/2.2-3300/" target="_blank" rel="noreferrer">2.2-3300</a> shall be construed to prevent or invalidate the entry, issuance, service or execution of any writ, summons, confession, judgment, order or decree, or other legal process whatever, or the session of the proceedings of any court or judge on any of the legal holidays or Saturdays nor to prevent any bank, banker, banking corporation, firm or association from keeping their doors open and transacting any lawful business on any of the legal holidays or Saturdays.</p><p>Code 1950, § 2-20; 1966, c. 677, § 2.1-22; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',3),s=[r];function i(c,l,d,h,g,_){return a(),o("div",null,s)}const f=e(t,[["render",i]]);export{u as __pageData,f as default};
