import{_ as e,o as a,c as i,O as t}from"./chunks/framework.9482e208.js";const f=JSON.parse(`{"title":"§ 2.2-3310 Vietnam War Memorial Dedication and Veterans' Recognition Week.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3310.md","filePath":"vacode/2.2-3310.md"}`),n={name:"vacode/2.2-3310.md"},r=t('<h1 id="§-2-2-3310-vietnam-war-memorial-dedication-and-veterans-recognition-week" tabindex="-1">§ 2.2-3310 Vietnam War Memorial Dedication and Veterans&#39; Recognition Week. <a class="header-anchor" href="#§-2-2-3310-vietnam-war-memorial-dedication-and-veterans-recognition-week" aria-label="Permalink to &quot;§ 2.2-3310 Vietnam War Memorial Dedication and Veterans&#39; Recognition Week.&quot;">​</a></h1><p>A. The first full week of November of each year shall be designated as Vietnam War Memorial Dedication Week and Veterans&#39; Recognition Week in the Commonwealth, to honor in perpetuity the men and women who have served their country as members of the armed forces during the Vietnam War.</p><p>B. In accordance with the Board of Education&#39;s guidelines as authorized by § <a href="http://law.lis.virginia.gov/vacode/22.1-17.4/" target="_blank" rel="noreferrer">22.1-17.4</a>, any veteran of the Vietnam War may apply for a Commonwealth of Virginia Vietnam War Veteran Honorary High School Diploma by filing with the Virginia Board of Education a statement declaring that:</p><ol><li><p>During the years between 1959 and 1975, he served in any branch of the United States Armed Forces and was subsequently honorably discharged;</p></li><li><p>He enlisted in or was drafted into the United States Armed Forces while still enrolled as a secondary school student in any school in any state or territory of the United States or any school located on or associated with a United States military base or embassy; and</p></li><li><p>He was unable to resume his secondary education upon returning to civilian life.</p></li></ol><p>C. Upon receiving a statement in compliance with this section and § <a href="http://law.lis.virginia.gov/vacode/22.1-17.4/" target="_blank" rel="noreferrer">22.1-17.4</a> that has been filed in accordance with its guidelines, the Board of Education shall award the veteran described in subsection B a Commonwealth of Virginia Vietnam War Veteran Honorary High School Diploma. Such diploma shall also be delivered during Virginia Vietnam War Memorial Dedication and Veterans&#39; Recognition Week in the Commonwealth.</p><p>1984, c. 691, § 2.1-27.3; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0684" target="_blank" rel="noreferrer">684</a>; 2009, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0066" target="_blank" rel="noreferrer">66</a>.</p>',6),o=[r];function l(s,d,c,h,m,g){return a(),i("div",null,o)}const _=e(n,[["render",l]]);export{f as __pageData,_ as default};
