import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-3315.1 White Cane Safety Day.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3315.1.md","filePath":"vacode/2.2-3315.1.md"}'),o={name:"vacode/2.2-3315.1.md"},s=i('<h1 id="§-2-2-3315-1-white-cane-safety-day" tabindex="-1">§ 2.2-3315.1 White Cane Safety Day. <a class="header-anchor" href="#§-2-2-3315-1-white-cane-safety-day" aria-label="Permalink to &quot;§ 2.2-3315.1 White Cane Safety Day.&quot;">​</a></h1><p>Each year, the Governor may take suitable public notice of October 15 as White Cane Safety Day. He may issue a proclamation in which:</p><ol><li><p>He comments upon the significance of the white cane;</p></li><li><p>He calls upon the citizens of the Commonwealth to observe the provisions of the White Cane Law and to take precautions necessary to the safety of the disabled;</p></li><li><p>He reminds the citizens of the Commonwealth of the policies with respect to the disabled herein declared and urges the citizens to cooperate in giving effect to them; and</p></li><li><p>He emphasizes the need of the citizens to be aware of the presence of disabled persons in the community and to keep safe and functional for the disabled the streets, highways, sidewalks, walkways, public buildings, public facilities, other public places, places of public accommodation, amusement and resort, and other places to which the public is invited, and to offer assistance to disabled persons upon appropriate occasions.</p></li></ol><p>1972, c. 156, § 63.1-171.5; 2002, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>.</p>',4),n=[s];function c(r,h,l,p,d,f){return t(),a("div",null,n)}const u=e(o,[["render",c]]);export{m as __pageData,u as default};
