import{_ as e,o as a,c as i,O as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 2.2-3319 American Indian Month and Day of Appreciation.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3319.md","filePath":"vacode/2.2-3319.md"}'),t={name:"vacode/2.2-3319.md"},o=n('<h1 id="§-2-2-3319-american-indian-month-and-day-of-appreciation" tabindex="-1">§ 2.2-3319 American Indian Month and Day of Appreciation. <a class="header-anchor" href="#§-2-2-3319-american-indian-month-and-day-of-appreciation" aria-label="Permalink to &quot;§ 2.2-3319 American Indian Month and Day of Appreciation.&quot;">​</a></h1><p>The month of November shall be designated as &quot;American Indian Month&quot; in Virginia, to honor the culture and heritage of the American Indian, to recognize the historic and continuing contribution of that heritage to American society, and in particular to commemorate the special place of the tribes native to Virginia in the life and history of the Commonwealth. Further, the Wednesday immediately preceding Thanksgiving of each year is designated a special Day of Appreciation for American Indians residing in the Commonwealth.</p><p>1987, c. 292, § 2.1-27.4; 1988, c. 685; 1996, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP1020" target="_blank" rel="noreferrer">1020</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0451" target="_blank" rel="noreferrer">451</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',3),r=[o];function c(d,h,s,l,p,f){return a(),i("div",null,r)}const g=e(t,[["render",c]]);export{_ as __pageData,g as default};
