import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-3707.01 (Effective September 1, 2022) Meetings of the General Assembly.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3707.01.md","filePath":"vacode/2.2-3707.01.md"}'),i={name:"vacode/2.2-3707.01.md"},s=r('<h1 id="§-2-2-3707-01-effective-september-1-2022-meetings-of-the-general-assembly" tabindex="-1">§ 2.2-3707.01 (Effective September 1, 2022) Meetings of the General Assembly. <a class="header-anchor" href="#§-2-2-3707-01-effective-september-1-2022-meetings-of-the-general-assembly" aria-label="Permalink to &quot;§ 2.2-3707.01 (Effective September 1, 2022) Meetings of the General Assembly.&quot;">​</a></h1><p>A. Except as provided in subsection B, public access to any meeting of the General Assembly or a portion thereof shall be governed by rules established by the Joint Rules Committee and approved by a majority vote of each house at the next regular session of the General Assembly. At least 60 days before the adoption of such rules, the Joint Rules Committee shall (i) hold regional public hearings on such proposed rules and (ii) provide a copy of such proposed rules to the Virginia Freedom of Information Advisory Council.</p><p>B. Floor sessions of either house of the General Assembly; meetings, including work sessions, of any standing or interim study committee of the General Assembly; meetings, including work sessions, of any subcommittee of such standing or interim study committee; and joint committees of conference of the General Assembly; or a quorum of any such committees or subcommittees, shall be open and governed by this chapter.</p><p>C. Meetings of the respective political party caucuses of either house of the General Assembly, including meetings conducted by telephonic or other electronic communication means, without regard to (i) whether the General Assembly is in or out of regular or special session or (ii) whether such caucuses invite staff or guests to participate in their deliberations, shall not be deemed meetings for the purposes of this chapter.</p><p>D. No regular, special, or reconvened session of the General Assembly held pursuant to Article IV, § <a href="http://law.lis.virginia.gov/constitution/article4/section6/" target="_blank" rel="noreferrer">6</a> of the Constitution of Virginia shall be conducted using electronic communication means pursuant to § <a href="/vacode/2.2-3708.2/">2.2-3708.2</a> or <a href="http://law.lis.virginia.gov/vacode/title2.2/chapter37/section2.2-3708.3/" target="_blank" rel="noreferrer">2.2-3708.3</a>.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0768" target="_blank" rel="noreferrer">768</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0352" target="_blank" rel="noreferrer">352</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0055" target="_blank" rel="noreferrer">55</a>; 2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0597" target="_blank" rel="noreferrer">597</a>.</p>',6),a=[s];function n(l,c,h,f,p,m){return t(),o("div",null,a)}const d=e(i,[["render",n]]);export{g as __pageData,d as default};
