import{_ as e,o as i,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-3900 Short title; declaration of policy.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-3900.md","filePath":"vacode/2.2-3900.md"}'),r={name:"vacode/2.2-3900.md"},l=a('<h1 id="§-2-2-3900-short-title-declaration-of-policy" tabindex="-1">§ 2.2-3900 Short title; declaration of policy. <a class="header-anchor" href="#§-2-2-3900-short-title-declaration-of-policy" aria-label="Permalink to &quot;§ 2.2-3900 Short title; declaration of policy.&quot;">​</a></h1><p>A. This chapter shall be known and cited as the Virginia Human Rights Act.</p><p>B. It is the policy of the Commonwealth to:</p><ol><li><p>Safeguard all individuals within the Commonwealth from unlawful discrimination because of race, color, religion, national origin, sex, pregnancy, childbirth or related medical conditions, age, marital status, sexual orientation, gender identity, military status, or disability in places of public accommodation, including educational institutions and in real estate transactions;</p></li><li><p>Safeguard all individuals within the Commonwealth from unlawful discrimination in employment because of race, color, religion, national origin, sex, pregnancy, childbirth or related medical conditions, age, marital status, sexual orientation, gender identity, disability, or military status;</p></li><li><p>Preserve the public safety, health, and general welfare;</p></li><li><p>Further the interests, rights, and privileges of individuals within the Commonwealth; and</p></li><li><p>Protect citizens of the Commonwealth against unfounded charges of unlawful discrimination.</p></li></ol><p>1987, c. 581, §§ 2.1-714, 2.1-715; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0404" target="_blank" rel="noreferrer">404</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1137" target="_blank" rel="noreferrer">1137</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1140" target="_blank" rel="noreferrer">1140</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0477" target="_blank" rel="noreferrer">477</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0478" target="_blank" rel="noreferrer">478</a>.</p>',5),n=[l];function o(c,s,d,h,p,g){return i(),t("div",null,n)}const m=e(r,[["render",o]]);export{u as __pageData,m as default};
