import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 2.2-4007 Petitions for new or amended regulations; opportunity for public comment.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4007.md","filePath":"vacode/2.2-4007.md"}'),n={name:"vacode/2.2-4007.md"},a=r('<h1 id="§-2-2-4007-petitions-for-new-or-amended-regulations-opportunity-for-public-comment" tabindex="-1">§ 2.2-4007 Petitions for new or amended regulations; opportunity for public comment. <a class="header-anchor" href="#§-2-2-4007-petitions-for-new-or-amended-regulations-opportunity-for-public-comment" aria-label="Permalink to &quot;§ 2.2-4007 Petitions for new or amended regulations; opportunity for public comment.&quot;">​</a></h1><p>A. Any person may petition an agency to request the agency to develop a new regulation or amend an existing regulation. The petition shall state (i) the substance and purpose of the rulemaking that is requested, including reference to any applicable Virginia Administrative Code sections, and (ii) reference to the legal authority of the agency to take the action requested.</p><p>B. Within 14 days of receiving a petition, the agency shall send a notice identifying the petitioner, the nature of the petitioner&#39;s request and the agency&#39;s plan for disposition of the petition to the Registrar for publication in the Virginia Register of Regulations in accordance with the provisions of subsection B of § <a href="http://law.lis.virginia.gov/vacode/2.2-4031/" target="_blank" rel="noreferrer">2.2-4031</a>.</p><p>C. A 21-day period for acceptance of written public comment on the petition shall be provided after publication in the Virginia Register. The agency shall issue a written decision to grant or deny the petitioner&#39;s request within 90 days following the close of the comment period. However, if the rulemaking authority is vested in an entity that has not met within that 90-day period, the entity shall issue a written decision no later than 14 days after it next meets. The written decision issued by the agency shall include a statement of its reasons and shall be submitted to the Registrar for publication in the Virginia Register of Regulations. Agency decisions to initiate or not initiate rulemaking in response to petitions shall not be subject to judicial review.</p><p>1984, c. 5, § 9-6.14:7.1; 1985, c. 602; 1989, c. 71; 1991, c. 488; 1993, cc. 898, 944; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0938" target="_blank" rel="noreferrer">938</a>; 1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0025" target="_blank" rel="noreferrer">25</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0677" target="_blank" rel="noreferrer">677</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0717" target="_blank" rel="noreferrer">717</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0790" target="_blank" rel="noreferrer">790</a>; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0241" target="_blank" rel="noreferrer">241</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0391" target="_blank" rel="noreferrer">391</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0747" target="_blank" rel="noreferrer">747</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0224" target="_blank" rel="noreferrer">224</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0619" target="_blank" rel="noreferrer">619</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0682" target="_blank" rel="noreferrer">682</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0873" target="_blank" rel="noreferrer">873</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0916" target="_blank" rel="noreferrer">916</a>.</p>',5),o=[a];function l(s,g,c,p,h,f){return t(),i("div",null,o)}const b=e(n,[["render",l]]);export{d as __pageData,b as default};
