import{_ as e,o as a,c as t,O as n}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 2.2-4030 Recovery of costs and attorney fees from agency.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-4030.md","filePath":"vacode/2.2-4030.md"}'),o={name:"vacode/2.2-4030.md"},r=n('<h1 id="§-2-2-4030-recovery-of-costs-and-attorney-fees-from-agency" tabindex="-1">§ 2.2-4030 Recovery of costs and attorney fees from agency. <a class="header-anchor" href="#§-2-2-4030-recovery-of-costs-and-attorney-fees-from-agency" aria-label="Permalink to &quot;§ 2.2-4030 Recovery of costs and attorney fees from agency.&quot;">​</a></h1><p>A. In any civil case brought under Article 5 (§ <a href="/vacode/2.2-4025/">2.2-4025</a> et seq.) or § <a href="/vacode/2.2-4002/">2.2-4002</a>, <a href="/vacode/2.2-4006/">2.2-4006</a>, <a href="/vacode/2.2-4011/">2.2-4011</a>, or <a href="/vacode/2.2-4018/">2.2-4018</a>, in which any person contests any agency action, such person shall be entitled to recover from that agency, including the Department of Wildlife Resources, reasonable costs and attorney fees if such person substantially prevails on the merits of the case and (i) the agency&#39;s position is not substantially justified, (ii) the agency action was in violation of law, or (iii) the agency action was for an improper purpose, unless special circumstances would make an award unjust. The award of attorney fees shall not exceed $25,000.</p><p>B. Nothing in this section shall be deemed to grant permission to bring an action against an agency if the agency would otherwise be immune from suit or to grant a right to bring an action by a person who would otherwise lack standing to bring the action.</p><p>C. Any costs and attorney fees assessed against an agency under this section shall be charged against the operating expenses of the agency for the fiscal year in which the assessment is made and shall not be reimbursed from any other source.</p><p>1981, c. 446, § 9-6.14:21; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0692" target="_blank" rel="noreferrer">692</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0625" target="_blank" rel="noreferrer">625</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0958" target="_blank" rel="noreferrer">958</a>.</p>',5),s=[r];function i(c,l,f,h,d,g){return a(),t("div",null,s)}const u=e(o,[["render",i]]);export{y as __pageData,u as default};
