import{_ as e,o as t,c as o,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-406 Secretary of Commonwealth to report list of interim appointments requiring confirmation; other appointments.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-406.md","filePath":"vacode/2.2-406.md"}'),r={name:"vacode/2.2-406.md"},a=n('<h1 id="§-2-2-406-secretary-of-commonwealth-to-report-list-of-interim-appointments-requiring-confirmation-other-appointments" tabindex="-1">§ 2.2-406 Secretary of Commonwealth to report list of interim appointments requiring confirmation; other appointments. <a class="header-anchor" href="#§-2-2-406-secretary-of-commonwealth-to-report-list-of-interim-appointments-requiring-confirmation-other-appointments" aria-label="Permalink to &quot;§ 2.2-406 Secretary of Commonwealth to report list of interim appointments requiring confirmation; other appointments.&quot;">​</a></h1><p>A. The Secretary of the Commonwealth shall periodically, during the interim between sessions of the General Assembly, present to the chairmen of the Senate and House Committees on Privileges and Elections a list of the names of all persons appointed by the Governor that require confirmation by the General Assembly. A list shall be presented by June 1, August 1, October 1, and December 1, and shall include the names of all persons so appointed since adjournment or since the last required report, the position to which appointed, and the person whom the appointee will succeed.</p><p>B. The Secretary of the Commonwealth shall report to the General Assembly by December 1 of each year, the number of persons appointed to any state board, commission, agency or authority, categorized by race, gender and national origin. Information on the race, gender and national origin of appointees shall be obtained through voluntary self-identification following appointment. Such information shall be used solely for the purpose of compiling the statistical information required under this section and any personally identifiable information collected under this section shall be confidential and shall be exempt from disclosure under the Freedom of Information Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-3700/" target="_blank" rel="noreferrer">2.2-3700</a> et seq.).</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0846" target="_blank" rel="noreferrer">846</a>, § 2.1-42.3; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0579" target="_blank" rel="noreferrer">579</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',4),i=[a];function s(l,p,c,h,m,f){return t(),o("div",null,i)}const g=e(r,[["render",s]]);export{u as __pageData,g as default};
