import{_ as t,o as e,c as r,O as o}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-409 Secretary of the Commonwealth.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-409.md","filePath":"vacode/2.2-409.md"}'),a={name:"vacode/2.2-409.md"},n=o('<h1 id="§-2-2-409-secretary-of-the-commonwealth" tabindex="-1">§ 2.2-409 Secretary of the Commonwealth. <a class="header-anchor" href="#§-2-2-409-secretary-of-the-commonwealth" aria-label="Permalink to &quot;§ 2.2-409 Secretary of the Commonwealth.&quot;">​</a></h1><p>A. The Secretary of the Commonwealth shall charge the following fees for services rendered in his office to be paid by the person for whom the service is rendered at the time it is done:</p><table><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td>a</td><td>For a testimonial, including seal tax....................................</td><td>$10.00</td></tr><tr><td>b</td><td>For each authentication after the first testimonial for documents bearing the testament by the same person on the same date, including seal tax...</td><td>5.00</td></tr><tr><td>c</td><td>For a copy of any paper, if on one sheet.................................</td><td>1.00</td></tr><tr><td>d</td><td>And for each sheet after the first.......................................</td><td>.75</td></tr><tr><td>e</td><td>For issuing a commission to a commissioner in another state..............</td><td>7.00</td></tr><tr><td>f</td><td>For power of attorney for nonresident insurers, contractors...............</td><td>3.00</td></tr><tr><td>g</td><td>For service of process on parties, each defendant........................</td><td>19.00</td></tr><tr><td>h</td><td>For service of process on reciprocal insurers.............................</td><td>7.00</td></tr><tr><td>i</td><td>For registration of name, badge and insignia............................</td><td>7.50</td></tr><tr><td>j</td><td>For affixing the Seal of the Commonwealth.............................</td><td>2.00</td></tr><tr><td>k</td><td>For issuing a commission to a notary for the Commonwealth at large, including seal tax..................................................</td><td>35.00</td></tr><tr><td>l</td><td>For issuing a commission to an electronic notary public...................</td><td>35.00</td></tr></tbody></table><p>And for filing in his office any paper required by law to be filed, the same fee as is allowed by law for recording similar papers.</p><p>B. Notwithstanding any other provision of law, the Secretary shall charge a technology fee of $10 in addition to the fees set out in subsection A for commissioning of a notary public or electronic notary public, which funds shall be deposited into the Secretary of the Commonwealth&#39;s Technology Trust Fund established by the comptroller and used only to obtain and update office automation and information technology equipment including software and conversion services; to preserve, maintain, and enhance records, including but not limited to the costs of repairs, maintenance, service contracts, and system upgrades; and to improve public access to records. There shall be no transfers out of the fund, including transfers to the general fund.</p><p>Code 1950, § 14-114; 1964, c. 386, § 14.1-103; 1966, c. 272; 1976, c. 559; 1979, c. 14; 1984, c. 362; 1987, cc. 449, 459; 1998, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0259" target="_blank" rel="noreferrer">259</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>, § 2.1-71.2; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2007, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0269" target="_blank" rel="noreferrer">269</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0362" target="_blank" rel="noreferrer">362</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0590" target="_blank" rel="noreferrer">590</a>.</p>',6),i=[n];function d(s,c,l,h,f,g){return e(),r("div",null,i)}const u=t(a,[["render",d]]);export{m as __pageData,u as default};
